INSERT INTO `extended_text` (`id`, `text`, `category`, `subcategory`, `author`, `tags`) VALUES ('anfrage_beschreibung', 'Bitte beschreibe das Problem so präzise wie möglich. \r\nVersuche auch eine ungefähre Einordnung der Anfragenart, da das	die Bearbeitung beschleunigen kann. \r\nWenn du Fragen zum Spiel hast, lies `bbitte`b zuerst die \r\n<a href=\\"petition.php?op=faq\\">`b`$FAQ`0`b</a>, die `$`bRegeln`b`0 und die Bücher in der `$`bDrachenbücherei`b`0 auf dem Dorfplatz. \r\n`nAnfragen, die das Spielgeschehen betreffen, werden i.d.R.\r\nnicht bearbeitet - es sei denn, sie haben etwas mit einem Fehler zu tun.`n\r\nAccounts werden nicht auf Wunsch gelöscht, diesbetreffende Anfragen sind zwecklos!', 'standard', 'Administratives', '', '');
