INSERT INTO `items_combos` (`combo_id`, `combo_name`, `id1`, `id2`, `id3`, `result`, `buff`, `type`, `chance`, `no_order`, `hook`, `hookcode`) VALUES (12, 'starkbier', 'klfale', 'klfale', 'klfale', 'strgale', 0, 2, 255, 0, '_codehook_', 'global $session,$item_hook_info,$combo;\r\n\r\nif($item_hook_info[''min_chance''] <= $combo[''chance'']) {\r\n$session[''user''][''drunkenness''] += 5;\r\n\r\n$item_hook_info[''victory_msg''] = ''Das wird mal n heftiges Starkbier! Allein die Dämpfe der Prozedur rufen in dir ein heftiges Gefühl der Trunkenheit hervor.'';\r\n\r\n}');
