INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (2, '`tWa`&ll`ta`&c`th`0', 'Das typische Arbeitspferd. Kräftig und ausdauernd, auch wenn ein Ritt auf ihm eher weniger bequem ist. Man kommt damit zwar schneller voran als mit einem Pony, aber ein Rennpferd ist es noch lange nicht.', 'Reittiere', 'a:6:{s:4:"name";s:19:"`tWa`&ll`ta`&c`th`0";s:6:"rounds";s:3:"100";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 20, 400, 1, 1, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (3, '`mH`6e`tng`6s`mt`0', 'Beliebt bei Boten und Kurieren ist der Hengst das günstigste, wirkliche Reitpferd. Zwar ist es noch immer kein Schlachtross, aber dafür edel, ausdauernd und schnell.', 'Reittiere', 'a:6:{s:4:"name";s:18:"`mH`6e`tng`6s`mt`0";s:6:"rounds";s:3:"120";s:6:"atkmod";s:4:"1.15";s:6:"defmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 30, 600, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (4, '`mH`qu`Qn`md`0', 'Der treueste aller Gefährten und noch dazu die passende Größe, um nirgends zu stören. Ja, sogar die meisten Tavernen haben sich auf den Besuch von Abenteurern mit ihren Hunden vorbereitet, ist der Abkomme der Wölfe kaum mehr aus dem Leben vieler Menschen wegzudenken. Ein hervorragender Wachposten, eine ideale Schulter zum Ausheulen oder eine passende Hilfe bei der Jagd: Der Hund darf nicht fehlen.', 'Haustiere', 'a:8:{s:4:"name";s:14:"`mH`qu`Qn`md`0";s:7:"wearoff";s:54:"`wDein Hund zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:62:"`wDein Hund trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"40";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"8";s:8:"activate";s:7:"offense";}', 0, 300, 1, 0, 0, '`wSchwanzwedelnd sitzt dein Hund vor dir und wartet schon auf neue Abenteuer.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (5, '`)M`7a`&us`0', 'Ein verhasster Feind der Taverneninhaber oder Zimmervermieter aller Schichten: Mäuse kommen überall hin oder durch und nagen alles an, was einem lieb ist. Gerade so groß, das ein jeder sie mit der Hand umschließen kann, versetzt sie manchmal ganze Häuser in Panik oder Wut. Gefräßige Haustiere wie diese können einem Mann helfen, schnell unliebsame Begegnungen mit Frauen zu unterbinden oder die ehemalige Frau zu verjagen. Am besten geeignet ist eine Maus jedoch zum Herumtragen und Kuscheln. Außerdem gibt es sie in verschiedenen Farben wie schwarz, weiß, braun oder grau.', 'Haustiere', 'a:8:{s:4:"name";s:12:"`)M`7a`&us`0";s:7:"wearoff";s:58:"`wDeine Maus verkrümelt sich irgendwo in deiner Rüstung...";s:9:"effectmsg";s:63:"`wDeine Maus trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"15";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"2";s:8:"activate";s:7:"offense";}', 0, 10, 1, 0, 0, '`wMit einem Stück Käse kommt deine Maus aus einem Loch gekrochen und verspeist dieses, ehe sie aufgeregt piepst.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (6, '`4Sch`mwa`erz`)e`er D`mra`4che`0', 'Wahrscheinlich einer der am gefährlichsten wirkenden Drachlinge die ich im Angebot habe und mit ihren 6 Metern Spannweits auch die größten noch zähmbaren. Sie besitzen keinen gefährlichen Odem, sind aber ungewöhnlich geschickt im Kampf und ihre langen messerscharfen Zähne sind in allen Reichen gefürchtet. Die Farbe ihrer Schuppen reicht von hellem grau, bis hin zu tiefstem Schwarz.', 'Drachlinge', 'a:8:{s:4:"name";s:32:"`4Sch`mwa`erz`)e`er D`mra`4che`0";s:7:"wearoff";s:66:"`wDein Schwarzer Drache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:74:"`wDein Schwarzer Drache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"200";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"22";s:15:"maxbadguydamage";s:2:"30";s:8:"activate";s:7:"offense";}', 75, 12000, 1, 0, 0, '`wDer Schwanz deines schwarzen Drachen peitscht ungeduldig und energisch auf den Boden. Er will endlich kämpfen und natürlich fressen.', '', '', 0, 0, 0, '', '', '', 15, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (7, '`7F`&l`7ö`&h`7e`0', 'Jeder kennt sie oder hat zumindest von ihnen gehört: Flöhe. Bei Abenteurern, die Gefährten mit Fell besitzen oder sogar selbst welches tragen, sind sie verhasst, bei den Feinden dieser Partei umso beliebter. Die kaum mehr größer als 1,5 mm großen Flöhe sind ein Muss für jeden, der sich dauerhaft Tiere vom Leibe halten oder andere zur Weißglut bringen will, da sie auch auf einer Distanz von 1 m noch ihr Ziel erreichen.', 'Haustiere', 'a:8:{s:4:"name";s:17:"`7F`&l`7ö`&h`7e`0";s:8:"roundmsg";s:47:"`wDein Gegner ist ein Festmahl für deine Flöhe!";s:7:"wearoff";s:24:"`wDeine Flöhe sind satt!";s:6:"rounds";s:2:"10";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.9";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 1, 100, 1, 0, 0, '`wDu wachst morgens auf und musst dich überall kratzen. Sofort weißt du, dass deine Flöhe schon lange nicht mehr schlafen.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (8, '`&E`#i`3n`#h`3o`#r`&n`0', 'Jeder kennt es, noch mehr wollen es: Das Einhorn. Von edlem Gemüt und ruhiger Natur ist dieses weise Geschöpf der Traum vieler Frauen, aber auch Männer. Sagenumwoben ist das Horn dieser Tiere, das auf der Stirn ihres Hauptes thront. Etwas kleiner als ein gewöhnliches Reitpferd, ist das Einhorn diesem sehr ähnlich, doch gespaltene Hufe und bei männlichen Einhörnern ein Bärtchen am Kinn unterscheiden das Einhorn deutlich von den anderen Pferden, vor allem aber, da sein Horn eine heilende Wirkung besitzt.', 'Reittiere', 'a:9:{s:4:"name";s:23:"`&E`#i`3n`#h`3o`#r`&n`0";s:7:"wearoff";s:43:"`wDein Einhorn zieht sich erschöpft zurück!";s:9:"effectmsg";s:79:"`wDie reine Aura deines Einhorns lässt dich {damage} Lebenspunkte regenerieren!";s:6:"rounds";s:3:"180";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:3:"1.1";s:5:"regen";s:1:"5";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 75, 10000, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 10, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (9, '`WSch`)a`ltte`)n`Wdr`)a`lc`)h`We`0', 'Rauch und Nebel. Keine greifbare Form und nichts als schemenhafte Erscheinungen um ihre zornigen und rot glühenden Augen. Eigentlich sind Schattendrachen keine wirklichen Drachen, sondern eine Art Dämon aus einer anderen Welt jenseits unserer Vorstellungskraft. Erst kurz vor ihrem todbringenden Biss, nehmen sie die feste Gestalt eines über 6 Meter langen, gewaltig wirkenden schwarzen Drachen ein.', 'Drachlinge', 'a:9:{s:4:"name";s:34:"`WSch`)a`ltte`)n`Wdr`)a`lc`)h`We`0";s:7:"wearoff";s:64:"`wDein Schattendrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:72:"`wDein Schattendrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"35";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"60";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 99, 50000, 1, 0, 0, '`wSchon als du deinen Schlafplatz verlässt, merkst du, dass du nicht mehr allein bist. Dein Drache folgt dir in den Schatten und nur seine glühenden Augen kannst du sehen.', '', '', 0, 0, 0, '', '', '', 30, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (10, '`TSch`7we`&r`7e`Ts K`7am`&pf`7ro`Tss`0', 'Genau das richtige Reittier für die großen Krieger unter euch. Seine Ausdauer ist nahezu unübertrefflich und seine Ausbildung bringt euch gute Vorteile in jedem Kampf.', 'Reittiere', 'a:6:{s:4:"name";s:38:"`TSch`7we`&r`7e`Ts K`7am`&pf`7ro`Tss`0";s:6:"rounds";s:3:"200";s:6:"atkmod";s:4:"1.15";s:6:"defmod";s:3:"1.1";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 50, 1000, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (11, '`&Specter`0', 'Geisterhafter Schemen aus den Tiefen des verlassenen Schlosses herauf beschworen. Sie lassen sich nicht lange in unserer Welt halten, wodurch ihre Kräfte schnell schwinden. Trotzdem ist allein ihr Anblick bereits für viele Gegner Grund genug, das Weite zu suchen.', 'Geister und Dämonen', 'a:9:{s:4:"name";s:11:"`&Specter`0";s:8:"roundmsg";s:67:"`wDer Anblick des Specters lässt deinen Gegner vor Angst erzittern!";s:7:"wearoff";s:56:"`wDein Specter zieht sich zurück in seine eigene Sphäre!";s:6:"rounds";s:2:"25";s:6:"defmod";s:3:"0.8";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.75";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 10, 100, 1, -1, 0, '`wVom Schloss her kommt dein Specter auf dich zu. Wie viele Wesen er heute schon mit seinem Anblick verschreckt hat?', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (12, '`4P`$h`qo`6e`^n`7i`&x`0', 'Text folgt noch.', 'Magische Wesen', 'a:8:{s:4:"name";s:23:"`4P`$h`qo`6e`^n`7i`&x`0";s:8:"roundmsg";s:61:"`wDein Phoenix verleiht dir ein Stück seiner Unsterblichkeit!";s:7:"wearoff";s:44:"`w Dein Phoenix zieht sich erschöpft zurück!";s:9:"effectmsg";s:40:"`wDu regenerierst {damage} Lebenspunkte!";s:6:"rounds";s:3:"110";s:5:"regen";s:1:"5";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 50, 8000, 1, 0, 0, '`wAnmutig schwebt dein Phoenix an dir vorbei, als du dich schon auf den Weg in den Wald machst. Beruhigt stellst du fest, dass der Vogel dir auch heute wieder seine treuen Dienste zur Verfügung stellt.', '', '', 0, 0, 0, '', '', '', 10, 1.14);
