INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (14, '`vW`7y`Vv`7e`vr`0', 'Eine gut 3 Meter lange, schlangenähnliche Kreatur. Knapp einen Meter unterhalb des Kopfes, der mehr dem eines Drachen ähnelt, besitzt die Wyver kleine, kaum brauchbare Flügel, welche meist eng an ihren schlanken Körper angelegt sind.', 'Drachlinge', 'a:8:{s:4:"name";s:17:"`vW`7y`Vv`7e`vr`0";s:7:"wearoff";s:56:"`wDeine Wyver zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:64:"`wDeine Wyver trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"110";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 10, 1000, 1, 0, 0, '`wMühsam schlängelt sich deine Wyver aus ihrem Erdloch und versucht, mit ihren verkümmerten Flügeln zu schlagen. Sie ist bereit für die Jagd.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
