INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (28, '`@F`2ee`knd`grac`2h`@e`0', 'Eine sehr seltene, in den verschiedensten Grüntönen schimmernde Kreatur aus den Elfenreichen im Westen. Feendrachen werden nicht einmal 2 Meter lang und anstatt der üblichen Schwingen besitzen sie Flügel, die denen einer übergroßen Libelle gleichen. Sie sind weder gute Kämpfer, noch besitzen sie einen magischen Odem, doch munkelt man sie hätten einen regenerativen Einfluss auf ihre Umgebung.', 'Drachlinge', 'a:10:{s:4:"name";s:24:"`@F`2ee`knd`grac`2h`@e`0";s:8:"roundmsg";s:62:"`wDu spürst die alles regenerierende Aura deines Feendrachens!";s:7:"wearoff";s:60:"`wDein Feendrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:68:"`wDein Feendrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"150";s:5:"regen";s:1:"2";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"10";s:8:"activate";s:7:"offense";}', 50, 25000, 1, 0, 0, '`wDein Feendrache schwirrt ungeduldig durch die Baumkronen im Wald. Anscheinend kann er es nicht abwarten, bis du ihm folgst, um gemeinsam zu kämpfen.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (29, '`$Va`4m`$pi`4rfle`$de`4rma`$us`0', 'Anders als normale Fledermäuse ist diese Fledermaus für jedermann gefährlich, da sie auch Menschen, Elfen oder ähnliches attackiert. Ganz wie ihre Namensvetter saugt die Vampirfledermaus ihren Opfern einiges an Blut aus, um zu überleben. Insbesondere Vampire wünschen die Gegenwart der pelzigen Fledermäuse mit ihren Leder ähnlichen Flügeln und ihrer graubraunen Farbe.', 'Magische Wesen', 'a:7:{s:4:"name";s:32:"`$Va`4m`$pi`4rfle`$de`4rma`$us`0";s:7:"wearoff";s:28:"`wDeine Fledermaus ist satt!";s:9:"effectmsg";s:63:"`wDank deiner Fledermaus regenerierst du {damage} Lebenspunkte!";s:6:"rounds";s:2:"60";s:11:"minioncount";s:1:"1";s:7:"lifetap";s:1:"1";s:8:"activate";s:10:"roundstart";}', 25, 1000, 1, 0, 0, '`wKurz nachdem die Nacht hereingebrochen ist, öffnet deine Fledermaus schlagartig ihre wachsamen Augen. Es ist nicht schwer zu erkennen, dass es sie nach Blut dürstet.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (30, '`1H`9a`&rp`9y`1e`0', 'Fast wunderschön ist die Frau anzusehen mit den Federschwingen, doch bei genauerem Hinsehen fallen die groben Unterschiede auf: Die prachtvollen Flügel sind fest mit den gelenken Armen verwachsen, die Füße sind nicht mehr als Vogelklauen. Eine unterhaltsame Begleitung für jeden Mann und auch als Kampfgefährtin wirklich nützlich.', 'Magische Wesen', 'a:9:{s:4:"name";s:18:"`1H`9a`&rp`9y`1e`0";s:7:"wearoff";s:57:"`wDeine Harpye zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:65:"`wDeine Harpye trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"80";s:6:"defmod";s:4:"0.95";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 15, 1000, 1, 0, 0, '`wDie Flügel der Kreatur umspielen dich sanft, doch sobald du den hinterlistigen Gesichtsausdruck siehst, merkst du, dass deine Harpye nur endlich auf Jagd gehen will.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (31, '`TGolem`0', 'Text folgt noch.', 'Magische Wesen', 'a:8:{s:4:"name";s:9:"`TGolem`0";s:7:"wearoff";s:29:"`wDein Golem zerfällt wieder!";s:9:"effectmsg";s:60:"`wDein Golem trifft den Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"80";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"35";s:8:"activate";s:7:"offense";}', 30, 2500, 1, -1, 0, '`wDu entdeckst deinen Golem hinter dem Haus, wo er sich einfach hingehockt hat und wohl die ganze Nacht so unsinnig in die Luft gestarrt hat, um auf dich zu warten.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (32, '`^Ska`6r`&ab`6ä`^us`0', 'Text folgt noch.', 'Magische Wesen', 'a:7:{s:4:"name";s:21:"`^Ska`6r`&ab`6ä`^us`0";s:7:"wearoff";s:44:"`wDie Kraft deines Skarabäus ist verbraucht!";s:9:"effectmsg";s:83:"`wDie magische Aura deines Skarabäus lässt dich {damage} Lebenspunkte regenerieren!";s:6:"rounds";s:2:"40";s:5:"regen";s:1:"5";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 30, 0, 1, -1, 0, '`wKaum ist dein Skarabäus munter, hast du schon alle Mühe ihn in der Hand zu halten und vor allem vor neidischen Blicken zu schützen, denn der kleine Käfer krabbelt aufgeweckt umher.', '', '', 100, 0, 100, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (33, '`QG`qo`tl`&d`tf`qi`Qsch`0', 'Eines der wahrscheinlich am leichtesten sauber zu haltenden Tiere, die ein Abenteurer sich wünschen kann. Ein Goldfisch ist nicht nur nett anzusehen - nein! - er ist auch der ideale Gefährte für einen Abenteurer, der nicht sehr redselig ist, aber auch nicht alleine sein will. Außer einem gelegentlichen Blubb wird er von dem Goldfisch nichts zu hören bekommen, geschweige denn Unterschützung bekommen.', 'Haustiere', 'a:5:{s:4:"name";s:25:"`QG`qo`tl`&d`tf`qi`Qsch`0";s:8:"roundmsg";s:26:"`wDein Goldfisch schwimmt!";s:6:"rounds";s:2:"42";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 0, 5, 1, 0, 0, '`wDein Goldfisch schwimmt einige Runden in seinem Glas und blubbert vor sich her.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (34, '`TSc`7hw`&ar`7ze`Tr Pa`7n`&th`7e`Tr`0', 'Text folgt noch.', 'Wildtiere', 'a:8:{s:4:"name";s:37:"`TSc`7hw`&ar`7ze`Tr Pa`7n`&th`7e`Tr`0";s:7:"wearoff";s:67:"`wDein schwarzer Panther zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:76:"`wDein schwarzer Panther trifft deinen Gegner mit {damage} Schadenspunkten! ";s:6:"rounds";s:3:"120";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"15";s:8:"activate";s:7:"offense";}', 12, 5000, 1, 0, 0, '`wDein schwarzer Panther streift ungeduldig durch den Wald. Er scheint bereits auf dich zu warten.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (35, '`4Erz`Qtro`^ll`0', '`2Der gewöhnliche `4Erz`Qtro`^ll`2 ist eine äußerst furchterregende Erscheinung.', 'Mod-Biester', 'a:14:{s:4:"name";s:8:"Erztroll";s:8:"roundmsg";s:61:"`2Keine Angst, dein `4Erz`Qtro`^ll`2 steht direkt hinter dir.";s:7:"wearoff";s:53:"`2Dein `4Erz`Qtro`^ll`2 verschwindet in seiner Höhle.";s:9:"effectmsg";s:72:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule und trifft den Gegner schwer";s:14:"effectnodmgmsg";s:70:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule.. in die falsche Richtung.";s:13:"effectfailmsg";s:70:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule.. in die falsche Richtung.";s:6:"rounds";s:3:"120";s:6:"atkmod";s:3:"1.2";s:6:"defmod";s:3:"1.1";s:5:"regen";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"10";s:7:"lifetap";s:1:"1";s:8:"activate";s:15:"offense,defense";}', 10000, 75, 0, 0, 1, '`2Dein `4Erz`Qtro`^ll`2 gibt ein Rülpsen von sich und tapst hinter dir her.', '`2Dein `4Erz`Qtro`^ll`2 reibt sich zufrieden den gigantischen Bauch.', '`2Dein `4Erz`Qtro`^ll`2 hat den halben Wald von Rehen befreit, ist aber immer noch nicht satt.', 100, 0, 100, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (36, '`6Re`^nn`tsch`^we`6in`0', 'Text folgt noch.', 'Reittiere', 'a:7:{s:4:"name";s:23:"`6Re`^nn`tsch`^we`6in`0";s:6:"rounds";s:2:"60";s:6:"atkmod";s:3:"0.9";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.75";s:8:"activate";s:10:"roundstart";}', 12, 500, 1, 3, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (37, '`TB`2au`@md`gra`@c`2h`Te`0', 'Baumdrachen sind eine kleine und weit weniger intelligente Version der großen grünen Drachen. Ihr Feueratem ist nicht wirklich beeindruckend, aber auf kurze Distanz dennoch sehr schmerzhaft. Baumdrachen erreichen eine Spannweit von bis zu 3 Metern.', 'Drachlinge', 'a:8:{s:4:"name";s:26:"`TB`2au`@md`gra`@c`2h`Te`0";s:7:"wearoff";s:60:"`wDein Baumdrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:68:"`wDein Baumdrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"90";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"15";s:8:"activate";s:7:"offense";}', 20, 2500, 1, 0, 0, '`wDein Baumdrache fliegt vom Wald her zu dir herüber. Hat er vielleicht versucht, den grünen Drachen ohne dich zu finden?', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (38, '`fPe`erl`sd`frac`eh`se`0', 'Wahrscheinlich einer der elegantesten Drachen im ganzen Kaiserreich. Ihr Körper erreicht eine Länge von annähernd 5 Metern und ist beinahe so schlank wie der einer Wyver. In ihrer Heimat an den westlichen Küsten, stürzen sie sich aus großer Höhe in die Fluten um ihre Lieblingsnahrung, frischen Fisch zu jagen, wobei sie sich unter Wasser nicht minder elegant bewegen, wie in der Luft.', 'Drachlinge', 'a:8:{s:4:"name";s:24:"`fPe`erl`sd`frac`eh`se`0";s:7:"wearoff";s:60:"`wDein Perldrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:68:"`wDein Perldrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"120";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 30, 5000, 1, 0, 0, '`wDein Perldrache liegt entspannt auf der Wiese und blickt dich erwartungsvoll an, um vielleicht einen Fisch von dir zu bekommen.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (39, '`3Ei`#sd`&ra`#c`3he`0', 'Eigentlich mehr ein Drachling und kein voll ausgewachsener Drache. Der eisige Atem dieser bis zu 5 Meter langen, silbrig schimmernden Kreaturen ist aber dennoch absolut tödlich für all jene, welche sich zu nahe an sie heran wagen.', 'Drachlinge', 'a:8:{s:4:"name";s:21:"`3Ei`#sd`&ra`#c`3he`0";s:7:"wearoff";s:59:"`wDein Eisdrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:67:"`wDein Eisdrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"160";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 75, 10000, 1, 0, 0, '`wDein Drache hat es sich im dunkelsten und kühlsten Abschnitt des Waldes gemütlich gemacht, wo er sich die Zeit, bis du kommst, mit kleinen Eisspielchen vertreibt.', '', '', 0, 0, 0, '', '', '', 15, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (40, '`4R`$o`&t`$e`4r D`$r`&ac`$h`4e`0', 'Ebenfalls nur ein Drachling. Eine kleinere Version ihrer großen Verwandten aus dem fernen Süden, deren feuriger Atem ganze Landstriche verheeren konnte. Diese Exemplare hier, sind mit ihren 5-6 Metern Spannweite noch lange nicht ausgewachsen, doch kann ihr Atem dennoch jeden Gegner ohne große Probleme garen lassen.', 'Drachlinge', 'a:8:{s:4:"name";s:32:"`4R`$o`&t`$e`4r D`$r`&ac`$h`4e`0";s:7:"wearoff";s:62:"`wDein Roter Drache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:70:"`wDein Roter Drache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"180";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 75, 10000, 1, 0, 0, '`wDein roter Drache kreist schon lange über dir, in der Hoffnung endlich jagen zu gehen.', '', '', 0, 0, 0, '', '', '', 15, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (41, '`6G`Vhu`6l`0', 'Jene unter euch, denen Ästhetik eher weniger wichtig ist, mögen den Ghul einem Skelettkrieger vorziehen. Sie sind ausdauernder und kräftiger, doch ist der stinkende und permanent verwesende, ehemals humanoide Körper nicht für jeden zu ertragen.', 'Geister und Dämonen', 'a:10:{s:4:"name";s:12:"`6G`Vhu`6l`0";s:7:"wearoff";s:54:"`wDein Ghul zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:62:"`wDein Ghul trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:1:"8";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 0, 2200, 1, -1, 0, '`wAls du den Gestank von Verwesung riechst und eine Schleimspur vor deinem Haus entdeckst, weißt du, dass dein Ghul nicht weit sein kann.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (42, '`5S`%u`&cc`%u`5b`&u`%s`0', 'Gebt auf euch Acht, solltet ihr euch wahrhaftig eines dieser verderbten und diabolischen Wesen erwerben wollen. Sie sind zwar sehr gute und ausdauernde Kämpfer, doch soll schon so mancher Möchtegern-Abenteurer ihnen in tiefer Nacht erlegen sein. Nicht ihren schaften Klingen, sondern ihrer unbeschreiblichen Schönheit. Letztenendes, führt aber beides bei Succubi zum Tode.', 'Geister und Dämonen', 'a:10:{s:4:"name";s:24:"`5S`%u`&cc`%u`5b`&u`%s`0";s:7:"wearoff";s:58:"`wDein Succubus zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:66:"`wDein Succubus trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"120";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"15";s:12:"badguyatkmod";s:3:"0.8";s:8:"activate";s:7:"offense";}', 40, 10000, 1, 0, 0, '`wEine Weile suchst du deinen Succubus, bis du ihn im Nebenzimmer mit einem schlafenden Mann findest. Allerdings musst du ihn erst davon überzeugen, nun mit dir jagen zu gehen.', '', '', 0, 0, 0, '', '', '', 10, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (43, '`1K`9o`&bo`4l`$d`0', 'Ein Exemplar auf das ich besonders stolz bin. Kobolde sind noch etwas kleiner aus Goblins und werden vielleicht einen halben Meter groß. Aber diese hier, sind nicht nur einfache Kobolde, sondern ihre magisch begabten Verwandten aus den Unterwelten. Sie können eure Gegner verfluchen und selbst aus geschickten Kriegern einen Tollpatsch machen.', 'Geister und Dämonen', 'a:8:{s:4:"name";s:18:"`1K`9o`&bo`4l`$d`0";s:8:"roundmsg";s:53:"`wLachend und kichernd wirkt dein Kobold seine Magie!";s:7:"wearoff";s:41:"`wPlötzlich ist dein Kobold verschwunden!";s:6:"rounds";s:2:"20";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.5";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 20, 1000, 1, 0, 0, '`wNeben dem Bett sitzt dein Kobold und sieht zu dir hoch. An seinem Blick kannst du erkennen, dass er schon wieder etwas ausgeheckt hat.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (44, '`RP`ri`&x`ri`re`0', 'Auch oft Waldfee genannt. Handgroße, humanoid erscheinende Kreaturen mit libellenartigen Flügeln. Sie sind weder gefährlich, noch in irgendeiner Weise aggressiv. Merricks Exemplare können zwar nicht sprechen, doch besitzen sie die gleichen magischen Fähigkeiten und die gleiche, alles regenerierende Aura, wie ihre Feenbrüder und Schwestern aus den Wäldern um Atrahor.', 'Magische Wesen', 'a:8:{s:4:"name";s:17:"`RP`ri`&x`ri`re`0";s:8:"roundmsg";s:42:"`wDeine Pixie wirkt ihre heilenden Zauber!";s:7:"wearoff";s:42:"`wDeine Pixie zieht sich erschöpft zurück!";s:9:"effectmsg";s:79:"`wDie magische Aura deiner Pixie lässt dich {damage} Lebenspunkte regenerieren!";s:6:"rounds";s:2:"80";s:5:"regen";s:1:"1";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 30, 5000, 1, 1, 0, '`wKaum bist du munter, schwirrt auch schon deine Pixie aufgeregt um dich herum und bereitet dir dadurch noch mehr Kopfschmerzen. Sie scheint ungeduldig auf neue Erlebnisse zu warten.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (45, '`1T`!od`1e`!sf`1ee`0', 'Die Banshee, oder auch Todesfee, tritt in Form einer alten, verwelkten Elfe mit eisig blasser Haut in Erscheinung. Beständig  scheint sie zu jammern und zu klagen, aber erst bei der Gefahr eines nahenden Gegners beginnt sie ihr wahres, Mark und Bein durchdringendes Klagelied, welches selbst gestandenen Helden den Mut auf alle Hoffnung zu nehmen vermag.', 'Geister und Dämonen', 'a:10:{s:4:"name";s:20:"`1T`!od`1e`!sf`1ee`0";s:8:"roundmsg";s:78:"`wDeine Todesfee singt ihr grausames, Mark und Bein durchdringendes Klagelied!";s:7:"wearoff";s:58:"`wTheatralisch lässt sich deine Todesfee zu Boden gleiten!";s:6:"rounds";s:2:"30";s:6:"atkmod";s:4:"0.95";s:6:"defmod";s:4:"0.95";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.5";s:12:"badguydefmod";s:4:"0.95";s:8:"activate";s:10:"roundstart";}', 35, 7500, 1, 0, 0, '`wVom Wald her hörst du wehleidiges Jammern und Klagen. Sofort weißt du, dass deine Todesfee sich schon ohne dich auf den Weg gemacht hat.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (46, '`6Kr`aö`8te`0', 'Warzige kleine Viecher, die Merrick einem örtlichen Hexenzirkel abschwatzen konnte. Viel gibt es zu diesen faustgroßen Tieren nicht zu sagen. Haltet sie stets feucht, denn ihre Sekrete verursachen nicht nur starken Juckreiz, sondern vermögen auch kleinere Wunden zu heilen.', 'Haustiere', 'a:6:{s:4:"name";s:13:"`6Kr`aö`8te`0";s:8:"roundmsg";s:73:"`wDu ignorierst das Jucken und verschmierst die Sekrete auf deine Wunden!";s:7:"wearoff";s:44:"`wDeine Kröte braucht dringend etwas Wasser!";s:6:"rounds";s:2:"10";s:5:"regen";s:1:"1";s:8:"activate";s:10:"roundstart";}', 1, 50, 1, 0, 0, '`wVom Teich vor deinem Haus hörst du schon deine Kröte quaken.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (47, '`$P`qa`^p`$a`^g`qe`$i`0', 'Ein bunter Vogel mit großem Schnabel...leider aber auch einem großen Mundwerk. Früher einmal von Piraten an den westlichen Küsten gehalten, kennt dieses Tier Schimpfworte und Begriffe, die noch jedem Schamesröte ins Gesicht treiben konnten. Merrick wird dir einen besonders guten Preis machen müssen...', 'Haustiere', 'a:7:{s:4:"name";s:23:"`$P`qa`^p`$a`^g`qe`$i`0";s:8:"roundmsg";s:78:"`wDein Papagei beleidigt deinen Gegner so lange, bis dieser anfängt zu weinen!";s:7:"wearoff";s:33:"`wDein Papagei hält den Schnabel!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.85";s:8:"activate";s:10:"roundstart";}', 5, 500, 1, 0, 0, '`wAuf einer Stange vor dir sitzt dein Papagei und krächzt immer wieder deinen Namen.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (48, '`TF`tr`Te`ttt`Tch`te`Tn`0', 'Klein, niedlich und hübsch anzusehen, liegen die Vorteile des Frettchens in seiner Statur. Es gibt keine Rüstung welche derart lückenlos ist, als dass nicht ein Frettchen noch hinein schlüpfen und einen wunden Punkt finden könnte...und ihre Bisse sind alles andere als angenehm.', 'Haustiere', 'a:8:{s:4:"name";s:25:"`TF`tr`Te`ttt`Tch`te`Tn`0";s:7:"wearoff";s:59:"`wDein Frettchen zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:67:"`wDein Frettchen trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"30";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"4";s:8:"activate";s:7:"offense";}', 0, 200, 1, 0, 0, '`wDein Frettchen beißt dir auffordernd in die Wade.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (49, '`7E`&s`7e`&l`0', 'Ein störrisches und nicht besonders flinkes Ding. Aber immerhin lässt es sich (zumindest manchmal) reiten und ist zudem noch ausgesprochen günstig. Genau das Richtige für den weniger betuchten Abenteurer.', 'Reittiere', 'a:5:{s:4:"name";s:14:"`7E`&s`7e`&l`0";s:6:"rounds";s:2:"60";s:6:"atkmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 5, 10, 1, 0, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (50, '`)S`7ke`slet`at-Ka`fmp`sfr`7os`)s`0', 'Einigen Totenbeschwörern ist es endlich gelungen, in Schlachten gefallene Schlachtrösser neu zu beseelen. Zwar sind sie weniger stark als ihre Lebenden Vorgänger, doch erfüllt der Anblick ihrer blanken Knochen so manchen Gegner mit Schrecken.', 'Reittiere', 'a:7:{s:4:"name";s:35:"`)S`7ke`slet`at-Ka`fmp`sfr`7os`)s`0";s:6:"rounds";s:3:"220";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:3:"1.1";s:15:"maxbadguydamage";s:3:"1.1";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 50, 1000, 1, 1, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (51, '`lRi`mes`)en`lspi`mnn`)e`0', 'Die großen Waldspinnen der östlichen Wälder. Ihr Biss ist nicht sehr giftig, weshalb es einigen Goblinstämmen gelungen ist diese 4-5 Meter großen Kreaturen zu zähmen. Haltet euch gut im Sattel fest, denn diese Biester können auch problemlos an Wänden oder Decken entlang laufen.', 'Reittiere', 'a:7:{s:4:"name";s:26:"`lRi`mes`)en`lspi`mnn`)e`0";s:6:"rounds";s:3:"120";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:4:"1.15";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 55, 2500, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 10, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (52, '`2Re`kit`@eide`kch`2se`0', 'Eigentlich ein nachtaktives und eher harmloses Tier von etwa 5 Metern Länge, dass sich von kleineren Säugern  und Amphibien ernährt. Im Kampf sollte man diese ungewöhnlich schnellen Kreaturen allerdings nicht unterschätzen. Ihre mit Saugnäpfen versehenen Füße lassen sie sich auf jedem Untergrund und sogar an Wänden und Decken gleichermaßen behende bewegen.', 'Reittiere', 'a:7:{s:4:"name";s:24:"`2Re`kit`@eide`kch`2se`0";s:6:"rounds";s:3:"150";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:3:"1.1";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.95";s:8:"activate";s:10:"roundstart";}', 40, 800, 1, 3, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (53, '`)S`&t`7e`&in`T-`)G`&a`7r`&go`7y`&le`0', 'Bei dieser Kreatur handelt es sich nicht nur um ein beeindruckendes Stück zwergischer Handwerkskunst, sondern auch um ein Meisterwerk der Beschwörungskunst. Zwar sind sie nicht intelligent und auch nicht besonders flink, aber dafür kommt einer ihrer Schläge schon gut dem Biss eines kleinen Drachen gleich.', 'Magische Wesen', 'a:8:{s:4:"name";s:38:"`)S`&t`7e`&in`T-`)G`&a`7r`&go`7y`&le`0";s:7:"wearoff";s:58:"`wDein Gargoyle zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:66:"`wDein Gargoyle trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"90";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"16";s:8:"activate";s:7:"offense";}', 10, 500, 1, 0, 0, '`wWie eine Statue hockt dein Stein-Gargoyle nahe des Hauses und erwacht erst zum Leben, als er das Wort ?Kampf? hört.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (54, '`4F`$e`Qu`qe`^r`q-`QG`$a`4rg`$o`Qy`ql`^e`0', 'Aus einem haselnussgroßen Rubin beschworen, handelt es sich hierbei um eine Kreatur aus reinstem Feuer, die ihre Gegner mit Feuerbällen, Flammenlanzen und anderen hitzigen Spielereien in die Knie zwingt. Aber Vorsicht! Schon so mancher Held hat sich selbst an diesen Biestern mehr als nur die Finger verbrannt.', 'Magische Wesen', 'a:11:{s:4:"name";s:42:"`4F`$e`Qu`qe`^r`q-`QG`$a`4rg`$o`Qy`ql`^e`0";s:8:"roundmsg";s:58:"`wDie flammende Aura deines Gargoyles versengt deine Haut!";s:7:"wearoff";s:65:"`wDein Gargoyle zerfällt und nur ein kleiner Rubin bleibt zurück!";s:9:"effectmsg";s:59:"`wDein x trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:6:"defmod";s:4:"0.95";s:5:"regen";s:2:"-1";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"15";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 10, 500, 1, 0, 0, '`wAls du von der Hitze geweckt wirst, wird dir sofort klar, dass dein Feuer-Gargoyle deine Umgebung bereits in Brand gesteckt hat und dass er dringend eine Beschäftigung benötigt.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (55, '`TDr`)i`7d`&er`0', 'Eine groteske und hässliche Kreatur, bestehend aus einem nackten Spinnenkörper, der mit Beinen einen Durchmesser von 3-4 Metern erreicht und an dessen Kopfende ein aufgedunsener und unwirklich erscheindender menschlicher Oberkörper aufragt. So abscheulich diese Kreatur aber anzusehen ist, so gefährlich ist sie auch im Kampf, denn sie vermag zwei Waffen in einem harmonischen und präzisen Einklag zu führen.', 'Magische Wesen', 'a:9:{s:4:"name";s:16:"`TDr`)i`7d`&er`0";s:7:"wearoff";s:56:"`wDein Drider zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:64:"`wDein Drider trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"120";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:2:"25";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 60, 7500, 1, 0, 0, '`wAngriffslustig schwingt der Drider seine Waffen, als er die ersten Anzeichen bemerkt, dass er bald kämpfen kann. Deshalb musst du schnell in Deckung gehen, um nicht selbst zu seinem Opfer zu werden.', '', '', 0, 0, 0, '', '', '', 10, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (56, '`9E`3u`#l`&e`0', 'Nur etwa 20-30cm messen diese nachtaktiven und eher harmlos erscheinenden Kreaturen. Bleibt abzuwarten was eure Gegner zu ihren ausdauernden und sturen Angriffen sagen werden...', 'Wildtiere', 'a:8:{s:4:"name";s:14:"`9E`3u`#l`&e`0";s:7:"wearoff";s:55:"`wDeine Eule zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:63:"`wDeine Eule trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"5";s:8:"activate";s:7:"offense";}', 0, 1200, 1, 0, 0, '`wHoch oben im Baum sitz deine Eule und fixiert geduldig ihre Beute.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (57, '`qK`to`eb`tr`qa`0', 'Unscheinbar in grau-braunen Farbtönen gehalten, ist dies eine der gefährlichsten Giftschlangen rund um Atrahor. Keine Rüstung ist lückenlos genug, um diese windige, bis zu einem halben Meter lange Kreatur abzuwehren. Ihr Gift selbst verursacht Krämpfe, Übelkeit und vorübergehende Sehschwäche. Also gebt Acht das ihr sie nicht reizt!', 'Wildtiere', 'a:9:{s:4:"name";s:17:"`qK`to`eb`tr`qa`0";s:8:"roundmsg";s:60:"`wDeine Kobra verschwindet unter der Rüstung deines Gegners!";s:7:"wearoff";s:56:"`wDeine Kobra zieht sich erschöpft aus dem Kampf zurück!";s:6:"rounds";s:1:"5";s:6:"defmod";s:3:"0.5";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.25";s:12:"badguydefmod";s:3:"0.5";s:8:"activate";s:10:"roundstart";}', 5, 100, 1, 0, 0, '`wDurch einen Biss tötet deine Kobra ein Beutetier und verschlingt es anschließend, bevor sie sich zu dir schlängelt.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (58, '`5A`%n`vac`&o`vn`%d`5a`0', 'Tödliche Stille herrscht um diese Schlange, die problemlos bis zu 9 Metern Größe erreicht. Deutlich zu unterscheiden von den anderen Schlangen ist sie durch ihre olivgrüne Färbung und dunklen Flecken, die wie Augen auf ihr nächstes Opfer stieren. Schneller als man es diesem Kriechtier zutraut, hat es schon zugebissen und ihr hilfloses Opfer vergiftet.', 'Wildtiere', 'a:9:{s:4:"name";s:24:"`5A`%n`vac`&o`vn`%d`5a`0";s:7:"wearoff";s:59:"`wDeine Anaconda zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:67:"`wDeine Anaconda trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"15";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"0";s:15:"maxbadguydamage";s:2:"30";s:8:"activate";s:7:"offense";}', 2, 2500, 1, 0, 0, '`wZischeln schlängelt sich deine Anakonda durch ihr Terrarium, wobei sie jede deiner Bewegungen verfolgt.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (59, '`4F`Qu`qc`th`&s`0', 'Der gemeine Rot- oder Waldfuchs. Klein, listig und flink. Gut zum Übermitteln von Botschaften (Fuchsen), aber, sofern ihr ihn gut behandelt, auch ein treuer Gefährte im Kampf.', 'Wildtiere', 'a:8:{s:4:"name";s:17:"`4F`Qu`qc`th`&s`0";s:7:"wearoff";s:55:"`wDein Fuchs zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:63:"`wDein Fuchs trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"60";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"5";s:8:"activate";s:7:"offense";}', 0, 1500, 1, 0, 0, '`wFür neue Abenteuer bereit, sitzt dein Fuchs schon vor seinem Bau und starrt hinüber zum Wald.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (60, '`TH`ty`7ä`&ne`0', 'Gefangen in den weiten Steppen der dunklen Lande, nördlich von Atrahor. Selbst mit guter Abrichtung sind sie noch immer feige und hinterhältig. Doch gerade diese Hinterhältigkeit macht sie zu so gefährlichen Gegnern und mit ihren starken und kräftigen Kiefer sind nicht zu unterschätzen.', 'Wildtiere', 'a:8:{s:4:"name";s:15:"`TH`ty`7ä`&ne`0";s:7:"wearoff";s:41:"`wDeine Hyäne zieht sich winselnd zurück!";s:9:"effectmsg";s:64:"`wDeine Hyäne trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"9";s:8:"activate";s:7:"offense";}', 0, 2000, 1, 0, 0, '`wAls du aus dem Haus trittst, bemerkst du deine Hyäne, wie sie sich gerade über die Überreste eines Vogels hermacht. Allerdings ist sie auch danach noch hungrig!', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (61, '`4Ja`Qg`qu`ta`&r`0', 'Zu einer der größten, aber dazu auch schnellsten Katze gehörend, ist der Jaguar der beste Partner für Jäger der freien Felder. Mit einer Gesamtlänge von bis zu 2 Metern zeugt diese große Raubkatze nicht nur von tödlicher Geduld und Anmut, sondern vielmehr, das sie schnell, effektiv und vor allem aber unberechenbar ist - nicht nur für Feinde. Wer einen Jaguar als Gefährten hat, muss sich darauf einstellen, das dieser, so wie jede Katze, seinen eigenen, sturen Willen besitzt und sich ungern herumkommandieren lässt. Im Übrigen: Jaguar sind keine guten Kuschelfreunde. Wenn sie sich schon mal auf ihren Gefährten legen, wird es bei ungefähr 50 Pfund Gewicht etwas erdrückend.', 'Wildtiere', 'a:8:{s:4:"name";s:18:"`4Ja`Qg`qu`ta`&r`0";s:7:"wearoff";s:56:"`wDein Jaguar zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:64:"`wDein Jaguar trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"4";s:15:"maxbadguydamage";s:2:"14";s:8:"activate";s:7:"offense";}', 6, 1000, 1, 0, 0, '`wRuhend liegt dein Jaguar vor deinen Füssen, nur darauf wartend, dass die Jagd beginnt.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
