INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (1, 'Vor mir fährt die Polizei, `nHinter mir ne Kutsche. `nRechts von mir ein Flugzeug, `nLinks von mir die Eisenbahn.`nWO BIN ICH?', 'Karusell');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (2, 'Welcher Igel,`n Eins, zwei, drei,`n Legt ein Mahagohni-Ei?', 'Kastanie');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (3, 'Was liegt auf dem Rasen,`n und hat 44 Nasen?', 'Rechen');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (4, 'Vorne wie ein Kamm,`n Mitten wie ein Lamm,`n Hinten wie ne Sichel,`n nun rat, mein lieber Michel.', 'Kamel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (5, 'Eines faulen Vaters Kind,`n und doch schneller als der Wind.', 'Licht');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (6, 'Was ich lebend steche tot,`n dem helf ich tot aus Todesnot.', 'Biene');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (7, 'Ri Ra Ripfel `n gelb ist der Zipfel, `n schwarz ist das Loch,`n wo man den Riraripfel drin kocht.', 'Erdäpfel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (8, 'Am Anfang bin ich das Gegenteil von Land,`n in der Mitte die heilige Schrift,`n danach eine Geliebte des Zeus`n und am Ende auch noch das Lied`n das den schönsten Platz für Biertrinker bezeichnet.`n Alles in allem bin ich eine sehr respektable Bildungs- und Unterhaltungseinrichtung.', 'Stadtbibliothek');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (9, 'Mit den Augen kann man ihn sehen,`n aber nicht mit den Händen greifen.', 'Schatten');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (10, 'Dort hängt es an der Wand,`n Das gibt mir jeden Morgen die Hand.', 'Handtuch');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (11, 'Solange ich bei meinem Herrn bleibe,`n helfe ich ihm nichts.`n Aber sobald er mich weggibt,`n da helfe ich ihm.', 'Geld');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (12, 'Was sieht aus wie eine Katze,`n miaut wie eine Katze,`n aber ist keine Katze?', 'Kater');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (13, 'Ein Tal voll und ein Land voll,`n Und am End ists keine Handvoll.', 'Nebel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (14, 'Wer es macht, der sagt es nicht,`n Wer es nimmt, der kennt es nicht,`n Wer es kennt, der nimmt es nicht.', 'Falschgeld');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (15, 'Er geht durch alle Gassen,`n klopft an Türen und Fenster`n und wird doch von niemand gesehen.', 'Wind');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (16, 'Wer besiegt selbst den stärksten Mann?', 'Schlaf');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (17, 'Es kann sprechen und auch stechen,`n ist zugleich ein Vogel und ein Gebrechen.', 'Star');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (19, 'Verfertigt ists vor langer Zeit,`n doch mehrenteils gemacht erst heut;`n sehr schätzbar ist es seinem Herrn,`n und dennoch hütets niemand gern.', 'Bett');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (20, 'Der es macht, der will es nicht;`n der es trägt, behält es nicht;`n der es kauft, der braucht es nicht;`n der es hat, der weiß es nicht.', 'Sarg');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (21, 'Es ist kein Haus,`n doch baut man es,`n man ißt es nicht,`n doch kaut man es,`n wenn mans nicht kaut,`n verbrennt man es.`n Ihr kennt es; sagt:`n Wie nennt man es?', 'Tabak');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (22, 'Immer ist es nah,`n niemals ist es da.`n Wenn Du denkst, Du seist daran,`n nimmt es andern Namen an.', 'morgen');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (23, 'Was ist nackter als nackt,`n so nackt, daß es knackt?', 'Skelett');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (24, 'Rat, wenn du kannst !`n Es nennen einen Wanst`n fünf Zeichen dir,`n und auch die letzten vier.', 'Bauch');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (25, 'Ich habe Flügel, rate Kind,`n Doch flieg ich nur im Kreise,`n Und singen tu ich, wenn der Wind`n Mir vorpfeift, laut und leise;`n Was ihr den Feldern abgewinnt,`n Kau ich auf meine Weise,`n Doch - was mir durch die Kehle rinnt,`n Das mundet euch als Speise.', 'Windmühle');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (26, 'Du brichst es,`n sobald du es nennst.', 'Stille');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (27, 'Mein Erstes, das ist nicht die Sonne.`n Mein Zweites bringt Wahres nicht ans Licht.`n Drum geb ich oft nur trügerische Wonne`n Und stets ein ungewisses Licht.', 'Mondschein');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (28, 'Ich helfe Kisten laden,`n doch mach ich auch Charaden.', 'Hebel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (29, 'Ich kenn ein warmes Haus,`n es hat drei Türen für rein und raus.`n Geht man morgens rein ins Haus,`n schauen unten Füße raus.`n Abends geht man wieder raus.`n Sag mir doch, wie heißt dies Haus?', 'Hose');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (30, 'Ich habe nur ein Angesicht.`n Es wird erhellt von fremdem Licht.`n Erhellts mich nicht,`n das fremde Licht,`n sieht man mich nicht.', 'Mond');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (31, 'Wenn du vorwärts mich beherrscht,`n darfst du fröhlich lachen;`n rückwärts kann dir alles sein,`n was die Gegner machen.', 'Lage; egal');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (32, 'Bin der Nachbar von Sonne und Sterne,`n regle das Wasser und tu dies sehr gerne.`n Bist Du verliebt, denkst Du müßtest verschmachten,`n Wirst Du die ganze Nacht mich betrachten.', 'Mond');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (33, 'Was sitzt still in einer Ecke,`n und reist doch um die ganze Welt?', 'Briefmarke');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (34, 'Was ist fertig und wird doch täglich neu gemacht?', 'Bett');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (35, 'Loch an Loch und hält doch!', 'Netz; Kette');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (36, 'Es tuts der Mond, die Sonne`n das Herz, das Eis, der Teig,`n die Tür, die Naht, die Blume,`n die Saat, die Knosp am Zweig.', 'aufgehen');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (37, 'Getrennt mir heilig -`n vereint abscheulich.', 'Meineid');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (38, 'Alle Tage geh ich aus,`n bleibe dennoch stets zu Haus.', 'Schnecke');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (39, 'Vier Stämperli, vier Plämperli,`n zwei Stuferli zwei Horcherli`n zwei Gugguggerli, ein Heurupferli,`n ein Grasmuffeli und ein Fliegenwädeli.', 'Kuh');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (40, 'Ich habe keine Füße`n und geh doch auf und ab`n und beiß mich immer tiefer ein,`n bis ich mich durchgebissen hab.', 'Säge');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (41, 'Es hat keine Flügel und fliegt doch`n es hat keinen Schnabel und beißt doch.', 'Wind');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (42, 'Wenn man es braucht,`n wirft man es weg,`n wenn man es nicht braucht,`n holt man es wieder zurück.', 'Anker');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (43, 'Kennst du mich`n so freut es dich;`n kennst du mich nicht`n so suche mich nur emsiglich:`n Du findest mich`n ganz sicherlich.', 'Lösung; Ergebnis');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (44, 'Man sieht es in der Mitte der Nacht,`n man träumt nicht davon,`n dennoch sieht man es im Schlaf.`n Am Tag kommt man ganz ohne es aus.', 'C; c');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (45, 'Nicht nur am Tag, nein, auch in finstrer Nacht,`n kann ohne Licht man meilenweit mich sehen.`n Doch wehe dem, dem ich zu nahe komm!`n Im Augenblick ist es um ihn geschehen.', 'Blitz');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (46, 'Vorwärts Trauer über Trauer,`n rückwärts Glück, doch ohne Dauer.', 'Sieg; Geist');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (47, 'Ich bin ein Gewinn`n bei Whist und Skat;`n und, läßt man dich drin,`n so schreist du: Verrat!', 'Stich');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (48, 'Im Häuschen mit fünf Stübchen,`n da wohnen braune Bübchen.`n Nicht Tor noch Tür führn ein und aus ,`n wer sie besucht, der ißt das Haus.`n Wo haben die Bübchen ihre fünf Stübchen?', 'Apfel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (49, 'Auf was ich vorwärts ihm mein Geld gegeben,`n ward rückwärts mir der Freund, da ich es wollt erheben.', 'Bank; Knab');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (50, 'Es bringt den Reiter um sein Roß,`nDen Edelmann um sein Schloß;`n Den Bauern um seinen Ackerpflug:`n Wer das errät, der ist wohl klug.', 'Würfelspiel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (51, 'Es hat zwar Flügel und kann nicht fliegen,`n hat einen Rücken und kann nicht liegen,`n es kann wohl laufen, aber nicht gehen.', 'Nase');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (52, 'Wer es tut mit "aus", der will verzichten,`n tust du es mit "ab", verweigerst du,`n tust du es mit "ein", so sagst du zu.`n Wer es tut mit "zer", der will vernichten,`n wer es tut mit "nach", will sich belehren,`n wer es tut mit "vor", den muß man hören,`n wer es tut mit "über", der ist flüchtig,`n wer es tut mit "be", der gilt als tüchtig.', 'schlagen');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (53, 'Die Erste ist das in trocknem Zustande,`n Was frisch als die andre wächst in dem Lande.`n Weh! wem das Ganze nur steht zu Gebot,`n Sich dran zu halten, kommt er in Not.', 'Strohhalm');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (54, 'Rätst du mich recht,`n so hast du falsch geraten;`n und rätst du falsch,`n so hast du recht geraten.', 'falsch');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (55, 'Die Ersten sind ein Untertan,`n Die Letzte ist ein Untertan,`n Das Ganze ist ein Untertan,`n Der von dem letzten Untertan`n Wird unter den ersten Untertan`n Ganz untertänigst getan.', 'Stiefelknecht');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (56, 'Es ist heiliger als Gottes Sohn`n und verwerflicher als Satan selbst.`n Die Toten essen es jeden Tag,`n doch essen es die Lebenden,`n so sterben sie langsam.', 'Nichts');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (57, 'Wenn man mich sieht, so sieht man mich nicht.`n Sieht man mich nicht, so sieht man mich.', 'Finsterniss; Dunkelheit');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (58, 'Mein Rock ist weiß wie Schnee`n und schwarz wie Kohlen.`n Was glänzt, das muß ich mir holen.', 'Elster');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (59, 'Sie schwitzt nicht bei den größten Hitzen,`n noch, wenn es draußen stürmt und schneit,`n dann sieht man sie zuweilen schwitzen.', 'Fensterscheibe; Fenster; Glasscheibe');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (60, 'Vers bin ich zur Hälfte,`n zur Hälfte nur Tand,`n errätst du mein Ganzes,`n so hast Du Verstand.', 'Verstand');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (61, 'Himmlische Tugend,`n scheußlicher Mord,`n Fehler im Kartenspiel -`n alles ein Wort.', 'vergeben');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (62, 'Zu Köln in der Dome,`nSteht eine gelbe Blume;`n Je länger sie steht`n Je mehr sie vergeht.', 'Kerze');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (63, 'Ich habe keinen Schneider`n und hab doch sieben Kleider.`n Wer mir sie auszieht,`n der muß weinen,`n und sollt er noch so lustig scheinen.', 'Zwiebel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (64, 'Das Erste zu halten ist oft schwer,`n Das andre ist Sache des Glücks gar sehr,`n Das Ganze ist nur ein schwarzer Zwerg`n Und hebt ganz leicht doch einen Berg.', 'Maulwurf');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (65, 'Ein einziges Mal nur bedürfen wir seiner,`n doch selber entlohnt hat ihn noch keiner.', 'Totengräber');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (66, 'Am ERSTEN ist es meist sehr kalt,`n das ZWEITE ist als Maß schon alt.`n Das GANZE ist oft eine Zier,`n doch in Gebrauch viel lieber mir.', 'Polster');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (67, 'Er ist berühmt,`n verdient viel Geld`n - ob Mann ob Frau -`n in der ganzen Welt.', 'Star');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (68, 'Zwei Löcher hab ich,`n zwei Finger brauch ich.`n So mach ich Langes und Großes klein`n und trenne, was nicht beisammen soll sein.', 'Schere');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (69, 'Steh ich davor, dann bin ich drin.`n Bin ich drin dann steh ich davor.', 'Spiegel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (70, 'Etwas, das alles und jeden verschlingt:`n Baum, der rauscht, Vogel, der singt,`n frißt Eisen, zermalmt den härtesten Stein,`n zerbeißt jedes Schwert, zerbricht jeden Schrein,`n Schlägt Könige nieder, schleift ihren Palast,`n trägt mächtigen Fels fort als leichte Last.', 'Zeit');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (71, 'Die Sprache kann es nicht entbehren,`n die Zeitung bringt es jeden Tag,`n der Kaufmann brauchts, will er sich nähren,`n und selten fehlt es im Vertrag.', 'Artikel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (72, 'Ich habe ein Loch und mach ein Loch`n und schlüpfe auch durch dieses noch.`n Kaum bin ich durch, stopf ichs im Nu`n mit meiner langen Schleppe zu.', 'Nadel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (73, 'Ich hab ein eigenes Schloß,`n doch ist das ziemlich klein.`n Es paßt kein Gast, kein Hausgenoß`n zugleich mit mir hinein.', 'Schlüssel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (74, 'Der Schrein ohne Deckel, Schlüssel, Scharnier,`n birgt einen goldenen Schatz, glaub es mir.', 'Ei');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (75, 'Er sah ein Weib, so schön wie keines noch auf Erden,`n gleich einem Wort mit "ö". Sie muß die Seine werden!`n Und bald auch konnt - o Glück! - er Wort mit "a" sie nennen.`n Da freilich lernt er sie von andrer Seite kennen.', 'Göttin; Gattin');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (76, 'Bei den Sängern guter Art`n rühmt man meine Milde.`n Wär'' ich in der Mitte hart,`n braus ich durchs Gefilde.', 'Organ; Orkan');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (77, 'Der Peter ist mit lautem Knallen,`n heut auf das Rätselwort gefallen.`n Zur Mutter humpelt er mit Weinen`n die gibt das Rätselwort dem Kleinen.', 'Pflaster');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (78, 'Ich bin die größte Straße`n zwischen Himmel und Erde.`n Kein Mensch ist hier gegangen,`n kein Wagen je gefahren.`n Und doch herrscht hier zwischen`n Erde und Himmel`n ein großes Gewimmel.', 'Milchstrasse; Milchstraße');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (79, 'Es geht und geht schon immerfort`n und kommt doch keinen Schritt vom Ort.', 'Uhr');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (80, 'Wer ist im Wald der kleine Mann,`n der nur auf einem Bein stehen kann?`n Hat einen großen bunten Hut,`n ist einmal giftig, einmal gut.', 'Pilz');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (81, 'Kommt zum Flächenmaß des Helden Tugend,`n dann drückt sie! Sie ist schwere Last,`n verdüstert manches Menschen Jugend`n und ist nicht reicher Leute Gast!', 'Armut');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (82, 'Bin ich Wasser, bin ich Luft,`n Bin ich Geist, bin ich Duft? --`n Etwas von dem allen;`n Fahr hinaus mit Gebraus,`n Und zu Saus und zu Braus`n Laß ichs auch noch knallen.', 'Champagner');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (83, 'Hab keinen Hals,`n auch keinen Kopf`n nicht Arm, noch Bein,`n ich armer Tropf.`n Mal bin ich voll,`n mal bin ich leer.`n Doch immer wiegt`n mein Holz sehr schwer.', 'Fass; Faß');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (84, 'Es schwebt daher ganz kugelrund,`n durchscheinend, leicht und herrlich bunt.`n Entstanden ists durch einen Hauch -`n lang lebt es nicht, bald platzt sein Bauch.', 'Seifenblase');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (85, 'Ihr lieben Leute,`n hab sieben Häute.`n Ich schone keinen,`n bring jeden zum Weinen.', 'Zwiebel');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (86, 'Das erste wächst an deinem Kopf,`n das zweite im Land der Zypressen.`n Das Ganze trifft dich unverhofft,`n hast du was ausgefressen.', 'Ohrfeige');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (87, 'Was Vögel tun, das sind gewisse Tiere,`n die nicht zwei Beine haben und nicht viere.', 'Fliegen');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (88, 'Je länger es bereits dauert,`n umso kürzer wird es.', 'Leben');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (89, 'Das Erste gibts für jedes Ding,`n das Zweite ist ein Wortgebilde;`n wer sich nicht an das Ganze halten kann,`n wird auch Opportunist genannt.', 'Grundsatz');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (90, 'Was ist das für ein Fuß,`n der immer zittern muß?', 'Hasenfuß');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (91, 'Er hat einen Kamm und kämmt sich nicht,`n Er hat Sporen und ist kein Ritter,`n Er hat eine Sichel und ist kein Schnitter.', 'Hahn');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (92, 'Wer es macht, der nennt es nicht.`nWer es sucht, der kennt es nicht.`nFindet er''s, wird''s hinterdrein`nnicht mehr, was es war, ihm sein.', 'Rätsel');
