INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (98, 'Wenn man mich sieht, so sieht man mich nicht.`nSieht man mich, so sieht man nicht.', 'Dunkelheit');
INSERT INTO `riddles` (`id`, `riddle`, `answer`) VALUES (99, 'Bin ich davor, dann bin ich darin;`nbin ich darin, dann bin ich davor.', 'Spiegel');
