INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (5, 'Raidho', 60, 'Alles verläuft rund, solange man sich nicht gegen die Erfordernisse der Zeit wehrt.', 'R', 'Lebens- und Weltenzyklen', 'r_raidho');
