INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (8, 'Wunjo', 100, 'Probleme sollten angepackt werden, statt über ihre Lösungen zu grübeln!', 'W', 'Ausgewogenheit', 'r_wunjo');
