# UPDATE DATEI
# Diese Datei enthält alle nötigen SQL Anfragen um eine bestehende 
# LOTGD Installation (0.9.7+jt ext(GER) Dragonslayer Edition V/2) 
# zu einer "0.9.7+jt ext(GER) Dragonslayer Edition V/2.5" zu machen.
# Führe alle enthaltenen Kommandos aus


# ADD:
# aei
ALTER TABLE account_extra_info 
ADD  `boatcoupons` smallint(5) unsigned NOT NULL default '0',
ADD  `csign` char(3) collate latin1_german1_ci NOT NULL default '',
ADD  `games_played` smallint(5) unsigned NOT NULL default '0',
ADD  `record` text collate latin1_german1_ci NOT NULL,
ADD  `donations` float unsigned NOT NULL default '0',
ADD  `chatallowed` tinyint(3) unsigned NOT NULL default '0',
  KEY `incommunity` (`incommunity`);

# account_stats
ALTER TABLE account_stats
  ADD  `browser` varchar(5) collate latin1_german1_ci NOT NULL default 'ukn',
  ADD  `browser_version` varchar(5) collate latin1_german1_ci NOT NULL default '0.0';

# accounts:
ALTER TABLE accounts 
  ADD  `daysinjail` int(11) NOT NULL default '0',
  ADD   `race_` char(3) collate latin1_german1_ci NOT NULL default '',
  MODIFY hashorse   `hashorse` smallint(5) unsigned NOT NULL default '0',
  MODIFY name   `name` varchar(80) collate latin1_german1_ci NOT NULL default '';
ALTER TABLE accounts
  ADD KEY `online` (`loggedin`,`laston`,`activated`);
ALTER TABLE accounts
  DROP KEY loggedin;
  
# Accounts - alte Rassen kopieren
UPDATE accounts SET race_ = 
(CASE race 
WHEN 1 THEN 'trl' WHEN 2 THEN 'elf' WHEN 3 THEN 'men' WHEN 4 THEN 'zwg' WHEN 5 THEN 'ecs' WHEN 6 THEN 'dkl' WHEN 7 THEN 'wwf' WHEN 8 THEN 'gbl' WHEN 9 THEN 'ork' WHEN 10 THEN 'vmp' WHEN 11 THEN 'hbl' WHEN 12 THEN 'dmn' WHEN 13 THEN 'slm' WHEN 14 THEN 'eng' WHEN 15 THEN 'avt' ELSE '' 
END);

# daysinjail updaten
UPDATE accounts SET daysinjail = 
(SELECT daysinjail FROM account_extra_info);

# daysinjail aus aei raus
ALTER TABLE account_extra_info 
DROP daysinjail;

# Alte Rassenspalte löschen, neue umbenennen
ALTER TABLE accounts 
	DROP COLUMN race,
	MODIFY race_ 'race' char(3) collate latin1_german1_ci NOT NULL default '';

