INSERT INTO `items_combos` (`combo_id`, `combo_name`, `id1`, `id2`, `id3`, `result`, `buff`, `type`, `chance`, `no_order`, `hook`, `hookcode`) VALUES (54, 'Killereichhörnchen', 'squirr', 'erdnuss', '', 'squirra', 0, 2, 250, 0, '_codehook_', 'global $item_hook_info;\r\n\r\n$bool_female = (bool)e_rand(0,1);\r\n$str_sex = ($bool_female ? ''Weibchen'' : ''Männchen'');\r\n$item_hook_info[''product''][''tpl_special_info'']\r\n= $str_sex;');
INSERT INTO `items_combos` (`combo_id`, `combo_name`, `id1`, `id2`, `id3`, `result`, `buff`, `type`, `chance`, `no_order`, `hook`, `hookcode`) VALUES (55, 'Todeshörnchen', 'ogerzahn', 'squirra', '', 'squirrb', 0, 2, 225, 0, '_codehook_', 'global $item_hook_info;\r\n\r\n$item_hook_info[''product''][''tpl_special_info'']\r\n= $item_hook_info[''items_in''][1][''special_info''];\r\n\r\nif (strpos($item_hook_info[''items_in''][1][''name''],"("))\r\n{\r\n$oldname=substr($item_hook_info[''items_in''][1][''name''],0,strpos($item_hook_info[''items_in''][1][''name''],"("));\r\ntrim($oldname);\r\n$item_hook_info[''product''][''tpl_name'']=$oldname." `&(`4Todes`thörnchen`0`&)";\r\n}\r\n');
