INSERT INTO `items_combos` (`combo_id`, `combo_name`, `id1`, `id2`, `id3`, `result`, `buff`, `type`, `chance`, `no_order`, `hook`, `hookcode`) VALUES (63, 'Mahlzeit', 'fleischbr', 'gewuerze', 'squirrd', '', 0, 2, 100, 1, '_codehook_', 'global $session;\r\n\r\noutput("`&Dir mundet deine Mahlzeit sehr und du erhält `@einem permanenten Lebenspunkt`& dazu und regenerierst voll!`n`n");\r\n\r\n$session[user][maxhitpoints]++;\r\n$session[user][hitpoints]=$session[user][maxhitpoints];\r\n');
INSERT INTO `items_combos` (`combo_id`, `combo_name`, `id1`, `id2`, `id3`, `result`, `buff`, `type`, `chance`, `no_order`, `hook`, `hookcode`) VALUES (64, 'Knappenrettung', 'elfknst', 'trph', 'zbrtafel', '', 0, 2, 100, 1, '_codehook_', 'global $session;\r\n\r\n$sql = "SELECT name,state,oldstate FROM disciples WHERE master=".$session[''user''][''acctid''];\r\n$result = db_query($sql) or die(db_error(LINK));\r\n$amount=db_num_rows($result);\r\nif ($amount<1)\r\n{\r\noutput("`@Der Zauber gelingt und vor dir steht ein junger Knabe, den du noch nie in deinem Leben gesehen hast!`n`n`&Du beschliesst den Jungen schnellstmöglich vor die Tür zu setzen, bevor man dich noch wegen Kindesentführung einsperrt!`n`0");\r\n}\r\nelse\r\n{\r\n$row = db_fetch_assoc($result);\r\nif ($row[''state'']>0)\r\n{\r\noutput("`@Der Zauber gelingt und vor dir steht ".$row[''name'']."`@ mit hochrotem Kopf und heruntergelassenen Hosen.`nDu hast den ärmsten wohl direkt von der Latrine zu dir gezaubert!`n`nDa euch beiden dieses Ereignis überaus peinlich ist versprecht ihr euch gegenseitig, nie wieder ein Wort darüber zu verlieren.`n`0");\r\n}\r\nelse\r\n{\r\noutput("`@Der Zauber gelingt und vor dir steht ".$row[''name'']."`@, sichtlich froh darüer, dich wiederzusehen.`nWohin er verschleppt wurde vermagst du nicht in Erfahrung zu bringen, aber er ist in bester Verfassung und scheint keinen bleibenden Schaden davon getragen zu haben.`n`nAllerding ist er von den Strapazen so sehr erschöpft, dass er dir wohl erst morgen wieder aktiv zur Seite stehen kann.`n`0");\r\n$sql = "UPDATE disciples SET state=$row[oldstate] WHERE master=".$session[user][acctid];\r\ndb_query($sql);\r\n}\r\n}');
INSERT INTO `items_combos` (`combo_id`, `combo_name`, `id1`, `id2`, `id3`, `result`, `buff`, `type`, `chance`, `no_order`, `hook`, `hookcode`) VALUES (65, 'Terrorhörnchen', 'squirra', 'wndsbl', '', 'squirrg', 0, 2, 200, 0, '_codehook_', 'global $item_hook_info;\r\n\r\n$item_hook_info[''product''][''tpl_special_info'']\r\n= $item_hook_info[''items_in''][0][''special_info''];\r\n\r\nif (strpos($item_hook_info[''items_in''][0][''name''],"("))\r\n{\r\n$oldname=substr($item_hook_info[''items_in''][0][''name''],0,strpos($item_hook_info[''items_in''][0][''name''],"("));\r\ntrim($oldname);\r\n$item_hook_info[''product''][''tpl_name'']=$oldname." `&(`&T`)e`&r`)r`&o`)r`thörnchen`0`&)";\r\n}\r\n');
