 ADD  KEY `activated` (`activated`);

# lib_themes:
ALTER TABLE lib_themes
 ADD   `description` text collate latin1_german1_ci NOT NULL;

REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (1, 'Über das Verhalten in dieser Gemeinschaft', '`tWeiter tragen dich deine Schritte, bis hin zu einem kleinen Regal an dem ein Schildchen darauf hinweist, dass hier Bücher zu finden sind, die darüber berichten, wie man sich in diesem Dorfe verhalten sollte. Sowohl nützliche Hinweise, die man besser beachten sollte, im Umgang mit den anderen Wesen dieses Dorfes wie auch Gesetze, deren Einhaltung streng überwacht wird, warten hier darauf, gelesen zu werden.', 2);
REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (2, 'Einführung in diese Welt', '`tLangsam gehst du an den Regalen entlang, gleich als erstes sticht dir eine Abteilung ins Auge, die wohl eindeutig für den neuen Krieger in dieser Welt geschaffen ist. Viele Werke über das Leben hier sollen das erste Zurechtfinden erleichtern und manch eine Frage beantworten, die dem abenteuerlustigen Neuankömmling, aber vielleicht auch einem erfahrenen Recken auf dem Herzen liegt. Ob Werke über das Training, die verschiedenen Orte und Ämter oder hilfreiche Tipps erfahrener Helden im Kampf gegen die grüne Bestie, zu fast jeder Lebenslage kann man hier Hilfe finden.', 1);
REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (3, 'Reiseberichte und Erlebnisse', '`tAuf weiten Regalen kannst du hier die Geschichten der Bürger dieses Dorfes finden, die diese niedergeschrieben haben. In großen, kleinen, schweren, ledergebundenen, neuen und fast zerfallenen Büchern ist so manches Leben festgehalten. Während du durch die Reihen gehst und die Titel betrachtest, fragst du dich, ob du hier nicht vielleicht sogar etwas über die Vergangenheit, das Schicksal manch eines Bekannten finden kannst.', 3);
REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (4, 'Mythen und Geschichten', '`tFast die Hälfte der hinterend Wand wird von Geschichten, Sagen und Mythen eingenommen. Zahllose Werke erzählen von Drachen und Jungfrauen, von Elfen, Zwergen und Menschen, von ewiger Liebe und bitteren Schlachten, von Geheimnissen und Legenden. Von Generation zu Generation weitergegeben, in der Schenke um die Ecke aufgeschnappt oder auch manchmal dem eigenen Geiste entsprungen bieten diese Bücher Kurzweil für fast jeden. Du kannst dir eines nehmen und es dir in einem der vielen Sessel gemütlich machen, aber denk dran, nicht alles zu ernst zu nehmen, was hier zwischen den Buchdeckeln auf dich wartet.', 4);
REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (5, 'Geschöpfe dieser Welt', '`tMit dem Vorsatz, dich ein wenig über die vielen Lebewesen in und um Atrahor zu informieren steuerst du eine gut sichtbare, große Abteilung der Bibliothek an. Buch an Buch, mal groß, mal klein, mal dick, mal dünn reiht sich hier aneinander, zahlreiche Weke über die bekannteren Rassen der in diesem Dorf heimischen Wesen, doch auch so einiges über die unbekannteren. Elfen, Zwerge, Drow, Hobbits, Drachen, Echsen, Nixen, keine Rasse scheint es zu geben, über die nicht irgendwer etwas zu sagen wusste. ', 5);
REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (6, 'Magien und Rituale', '`tAuf einem kleinen Regalbrett, eng zusammengrückt die Bücher über Magie und rituelle Handlungen. So manch einen Zauber kannst du hier schnell nachschlagen, doch solltest du immer auf der Hut sein, mit was du dich da befasst.', 6);
REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (8, 'Gilden und Hausausbauten', '`tEine Handvoll schwerer Bücher, gefüllt mit dem Wissen über Wohn- und Gildengebäude wartet gut sichtbar auf die Ratsuchenden, die Informationen über die verschiedenen Haustypen suchen oder sich über die Besonderheiten einer Gilde informieren wollen. Ob du nun schon ein eigenes Häuschen hast oder nur wissen willst, wo du am besten Unterschupf suchst, eine Gilde gegründet hast und dich noch nicht so ganz zurechtfindest oder keiner angehörst und dich vorher informieren willst, schlage diese Seiten auf und du wirst finden, was du suchst.', 7);
REPLACE `lib_themes` (`themeid`, `theme`, `description`, `listorder`) VALUES (9, 'Archiv', '`tSchon willst du die Bibliothek wieder verlassen, als dein Blick auf ein verstaubtes Regal im Hintergrund fällt. Neugierig näherst du dich ihm, bis du erkennen kannst, dass sich auch darauf Bücher befinden, welche jedoch in wesentlich schlechterem Zustand sind, als die in den restlichen Regalen. Veraltete und nie gelesene Weke fristen hier ihr Dasein, bis sie eines Tages unter der Berührung irgendeines neugierigen Abenteurers zu Staub zerfallen werden. Manch ein Buch aussortiert wegen längst falscher Angaben, verdrängt durch neuere, bessere Werke oder einfach zu uninteressant und unbeliebt, um Platz dafür in den übrigen Teilen des Raumes zu verschwenden. Eigentlich könntest du wieder gehen, doch interessant könnte es ja schon sein, zu sehen, was sich zwischen diesen vergessenen Buchdeckeln befindet.', 8);

# masters:

REPLACE `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (5, 'Unélith', 5, 'Gedankenkontrolle', 'Dein Geist ist stärker als meiner. Ich gebe auf.', 'Deine mentalen Kräfte haben noch Schwächen. Meditiere über dein Versagen und du wirst mich eines Tages besiegen', NULL, NULL, 55, 10, 10);

