# mounts:

REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (1, '`TP`to`&n`Ty`0', 'Es ist nicht unbedingt ein Schlachtross, außer natürlich man ist ein Halbling, aber noch immer günstig für ein Reittier. Treuer als der Esel und ausgesprochen gutmütig ist es alle Mal.', 'Reittiere', 'a:6:{s:4:"name";s:14:"`TP`to`&n`Ty`0";s:6:"rounds";s:2:"80";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 10, 200, 1, 1, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (2, '`tWa`&ll`ta`&c`th`0', 'Das typische Arbeitspferd. Kräftig und ausdauernd, auch wenn ein Ritt auf ihm eher weniger bequem ist. Man kommt damit zwar schneller voran als mit einem Pony, aber ein Rennpferd ist es noch lange nicht.', 'Reittiere', 'a:6:{s:4:"name";s:19:"`tWa`&ll`ta`&c`th`0";s:6:"rounds";s:3:"100";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 20, 400, 1, 1, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (3, '`mH`6e`tng`6s`mt`0', 'Beliebt bei Boten und Kurieren ist der Hengst das günstigste, wirkliche Reitpferd. Zwar ist es noch immer kein Schlachtross, aber dafür edel, ausdauernd und schnell.', 'Reittiere', 'a:6:{s:4:"name";s:18:"`mH`6e`tng`6s`mt`0";s:6:"rounds";s:3:"120";s:6:"atkmod";s:4:"1.15";s:6:"defmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 30, 600, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (4, '`mH`qu`Qn`md`0', 'Der treueste aller Gefährten und noch dazu die passende Größe, um nirgends zu stören. Ja, sogar die meisten Tavernen haben sich auf den Besuch von Abenteurern mit ihren Hunden vorbereitet, ist der Abkomme der Wölfe kaum mehr aus dem Leben vieler Menschen wegzudenken. Ein hervorragender Wachposten, eine ideale Schulter zum Ausheulen oder eine passende Hilfe bei der Jagd: Der Hund darf nicht fehlen.', 'Haustiere', 'a:8:{s:4:"name";s:14:"`mH`qu`Qn`md`0";s:7:"wearoff";s:54:"`wDein Hund zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:62:"`wDein Hund trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"40";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"8";s:8:"activate";s:7:"offense";}', 0, 300, 1, 0, 0, '`wSchwanzwedelnd sitzt dein Hund vor dir und wartet schon auf neue Abenteuer.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (5, '`)M`7a`&us`0', 'Ein verhasster Feind der Taverneninhaber oder Zimmervermieter aller Schichten: Mäuse kommen überall hin oder durch und nagen alles an, was einem lieb ist. Gerade so groß, das ein jeder sie mit der Hand umschließen kann, versetzt sie manchmal ganze Häuser in Panik oder Wut. Gefräßige Haustiere wie diese können einem Mann helfen, schnell unliebsame Begegnungen mit Frauen zu unterbinden oder die ehemalige Frau zu verjagen. Am besten geeignet ist eine Maus jedoch zum Herumtragen und Kuscheln. Außerdem gibt es sie in verschiedenen Farben wie schwarz, weiß, braun oder grau.', 'Haustiere', 'a:8:{s:4:"name";s:12:"`)M`7a`&us`0";s:7:"wearoff";s:58:"`wDeine Maus verkrümelt sich irgendwo in deiner Rüstung...";s:9:"effectmsg";s:63:"`wDeine Maus trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"15";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"2";s:8:"activate";s:7:"offense";}', 0, 10, 1, 0, 0, '`wMit einem Stück Käse kommt deine Maus aus einem Loch gekrochen und verspeist dieses, ehe sie aufgeregt piepst.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (6, '`4Sch`mwa`erz`)e`er D`mra`4che`0', 'Wahrscheinlich einer der am gefährlichsten wirkenden Drachlinge die ich im Angebot habe und mit ihren 6 Metern Spannweits auch die größten noch zähmbaren. Sie besitzen keinen gefährlichen Odem, sind aber ungewöhnlich geschickt im Kampf und ihre langen messerscharfen Zähne sind in allen Reichen gefürchtet. Die Farbe ihrer Schuppen reicht von hellem grau, bis hin zu tiefstem Schwarz.', 'Drachlinge', 'a:8:{s:4:"name";s:32:"`4Sch`mwa`erz`)e`er D`mra`4che`0";s:7:"wearoff";s:66:"`wDein Schwarzer Drache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:74:"`wDein Schwarzer Drache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"200";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"22";s:15:"maxbadguydamage";s:2:"30";s:8:"activate";s:7:"offense";}', 75, 12000, 1, 0, 0, '`wDer Schwanz deines schwarzen Drachen peitscht ungeduldig und energisch auf den Boden. Er will endlich kämpfen und natürlich fressen.', '', '', 0, 0, 0, '', '', '', 15, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (7, '`7F`&l`7ö`&h`7e`0', 'Jeder kennt sie oder hat zumindest von ihnen gehört: Flöhe. Bei Abenteurern, die Gefährten mit Fell besitzen oder sogar selbst welches tragen, sind sie verhasst, bei den Feinden dieser Partei umso beliebter. Die kaum mehr größer als 1,5 mm großen Flöhe sind ein Muss für jeden, der sich dauerhaft Tiere vom Leibe halten oder andere zur Weißglut bringen will, da sie auch auf einer Distanz von 1 m noch ihr Ziel erreichen.', 'Haustiere', 'a:8:{s:4:"name";s:17:"`7F`&l`7ö`&h`7e`0";s:8:"roundmsg";s:47:"`wDein Gegner ist ein Festmahl für deine Flöhe!";s:7:"wearoff";s:24:"`wDeine Flöhe sind satt!";s:6:"rounds";s:2:"10";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.9";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 1, 100, 1, 0, 0, '`wDu wachst morgens auf und musst dich überall kratzen. Sofort weißt du, dass deine Flöhe schon lange nicht mehr schlafen.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (8, '`&E`#i`3n`#h`3o`#r`&n`0', 'Jeder kennt es, noch mehr wollen es: Das Einhorn. Von edlem Gemüt und ruhiger Natur ist dieses weise Geschöpf der Traum vieler Frauen, aber auch Männer. Sagenumwoben ist das Horn dieser Tiere, das auf der Stirn ihres Hauptes thront. Etwas kleiner als ein gewöhnliches Reitpferd, ist das Einhorn diesem sehr ähnlich, doch gespaltene Hufe und bei männlichen Einhörnern ein Bärtchen am Kinn unterscheiden das Einhorn deutlich von den anderen Pferden, vor allem aber, da sein Horn eine heilende Wirkung besitzt.', 'Reittiere', 'a:9:{s:4:"name";s:23:"`&E`#i`3n`#h`3o`#r`&n`0";s:7:"wearoff";s:43:"`wDein Einhorn zieht sich erschöpft zurück!";s:9:"effectmsg";s:79:"`wDie reine Aura deines Einhorns lässt dich {damage} Lebenspunkte regenerieren!";s:6:"rounds";s:3:"180";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:3:"1.1";s:5:"regen";s:1:"5";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 75, 10000, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 10, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (9, '`WSch`)a`ltte`)n`Wdr`)a`lc`)h`We`0', 'Rauch und Nebel. Keine greifbare Form und nichts als schemenhafte Erscheinungen um ihre zornigen und rot glühenden Augen. Eigentlich sind Schattendrachen keine wirklichen Drachen, sondern eine Art Dämon aus einer anderen Welt jenseits unserer Vorstellungskraft. Erst kurz vor ihrem todbringenden Biss, nehmen sie die feste Gestalt eines über 6 Meter langen, gewaltig wirkenden schwarzen Drachen ein.', 'Drachlinge', 'a:9:{s:4:"name";s:34:"`WSch`)a`ltte`)n`Wdr`)a`lc`)h`We`0";s:7:"wearoff";s:64:"`wDein Schattendrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:72:"`wDein Schattendrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"35";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"60";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 99, 50000, 1, 0, 0, '`wSchon als du deinen Schlafplatz verlässt, merkst du, dass du nicht mehr allein bist. Dein Drache folgt dir in den Schatten und nur seine glühenden Augen kannst du sehen.', '', '', 0, 0, 0, '', '', '', 30, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (10, '`TSch`7we`&r`7e`Ts K`7am`&pf`7ro`Tss`0', 'Genau das richtige Reittier für die großen Krieger unter euch. Seine Ausdauer ist nahezu unübertrefflich und seine Ausbildung bringt euch gute Vorteile in jedem Kampf.', 'Reittiere', 'a:6:{s:4:"name";s:38:"`TSch`7we`&r`7e`Ts K`7am`&pf`7ro`Tss`0";s:6:"rounds";s:3:"200";s:6:"atkmod";s:4:"1.15";s:6:"defmod";s:3:"1.1";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 50, 1000, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (11, '`&Specter`0', 'Geisterhafter Schemen aus den Tiefen des verlassenen Schlosses herauf beschworen. Sie lassen sich nicht lange in unserer Welt halten, wodurch ihre Kräfte schnell schwinden. Trotzdem ist allein ihr Anblick bereits für viele Gegner Grund genug, das Weite zu suchen.', 'Geister und Dämonen', 'a:9:{s:4:"name";s:11:"`&Specter`0";s:8:"roundmsg";s:67:"`wDer Anblick des Specters lässt deinen Gegner vor Angst erzittern!";s:7:"wearoff";s:56:"`wDein Specter zieht sich zurück in seine eigene Sphäre!";s:6:"rounds";s:2:"25";s:6:"defmod";s:3:"0.8";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.75";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 10, 100, 1, -1, 0, '`wVom Schloss her kommt dein Specter auf dich zu. Wie viele Wesen er heute schon mit seinem Anblick verschreckt hat?', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (12, '`4P`$h`qo`6e`^n`7i`&x`0', 'Text folgt noch.', 'Magische Wesen', 'a:8:{s:4:"name";s:23:"`4P`$h`qo`6e`^n`7i`&x`0";s:8:"roundmsg";s:61:"`wDein Phoenix verleiht dir ein Stück seiner Unsterblichkeit!";s:7:"wearoff";s:44:"`w Dein Phoenix zieht sich erschöpft zurück!";s:9:"effectmsg";s:40:"`wDu regenerierst {damage} Lebenspunkte!";s:6:"rounds";s:3:"110";s:5:"regen";s:1:"5";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 50, 8000, 1, 0, 0, '`wAnmutig schwebt dein Phoenix an dir vorbei, als du dich schon auf den Weg in den Wald machst. Beruhigt stellst du fest, dass der Vogel dir auch heute wieder seine treuen Dienste zur Verfügung stellt.', '', '', 0, 0, 0, '', '', '', 10, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (13, '`TN`4a`$ch`Ttm`$a`4h`Tr`0', 'Text folgt noch.', 'Reittiere', 'a:9:{s:4:"name";s:25:"`TN`4a`$ch`Ttm`$a`4h`Tr`0";s:7:"wearoff";s:45:"`wDein Nachtmahr zieht sich erschöpft zurück!";s:9:"effectmsg";s:84:"`wDie finstere Aura deines Nachtmahrs lässt dich {damage} Lebenspunkte regenerieren!";s:6:"rounds";s:3:"100";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:3:"1.1";s:5:"regen";s:1:"3";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 60, 10000, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 10, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (14, '`vW`7y`Vv`7e`vr`0', 'Eine gut 3 Meter lange, schlangenähnliche Kreatur. Knapp einen Meter unterhalb des Kopfes, der mehr dem eines Drachen ähnelt, besitzt die Wyver kleine, kaum brauchbare Flügel, welche meist eng an ihren schlanken Körper angelegt sind.', 'Drachlinge', 'a:8:{s:4:"name";s:17:"`vW`7y`Vv`7e`vr`0";s:7:"wearoff";s:56:"`wDeine Wyver zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:64:"`wDeine Wyver trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"110";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 10, 1000, 1, 0, 0, '`wMühsam schlängelt sich deine Wyver aus ihrem Erdloch und versucht, mit ihren verkümmerten Flügeln zu schlagen. Sie ist bereit für die Jagd.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (15, '`TF`ta`Tlk`te`0', 'Prächtig und stolz ist dieser kleine Greifvogel mit den langen Schwanzfedern und spitzen Flügeln. Schnell wie kein anderer durchzieht der Falke den Himmel; und noch schneller ist seine Beute ergriffen und getötet. Da dieser Greifvogel bis zu 50 Jahre alt werden kann, ist er ein passender Begleiter für Menschen, die an ihren Gefährten hängen, zudem ist auch der Falke sehr anhänglich und ein treuer Begleiter in jeder Umgebung.', 'Wildtiere', 'a:8:{s:4:"name";s:15:"`TF`ta`Tlk`te`0";s:7:"wearoff";s:55:"`wDein Falke zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:63:"`wDein Falke trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"3";s:8:"activate";s:7:"offense";}', 0, 800, 1, 0, 0, '`wDein Falke zieht schon eine Weile über dir seine Kreise und wartet auf neue Abenteuer.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (16, '`7Sk`)e`8lett`ekr`)ie`7ge`8r`0', 'Mit Hilfe einer kurzen Zauberformel aus einer Handvoll alter Knochen beschworen, folgt euch der Skelettkrieger ohne zu fragen bis in den Tod. Sehr flink, aber dafür mit nicht einmal dem gerinsten Maß an Intelligenz gesegnet, sind sie nicht unbedingt die besten, aber dafür relativ günstige Gefährten.', 'Geister und Dämonen', 'a:9:{s:4:"name";s:30:"`7Sk`)e`8lett`ekr`)ie`7ge`8r`0";s:7:"wearoff";s:49:"`wDein Skelettkrieger zerfällt in etwa 200 Teile!";s:9:"effectmsg";s:72:"`wDein Skelettkrieger trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"80";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"5";s:12:"badguyatkmod";s:4:"0.95";s:8:"activate";s:7:"offense";}', 0, 2000, 1, -1, 0, '`wVon draußen hörst du das Knacken von Knochen und siehst kurz darauf, wie dein Skelettkrieger mit seinem Kopf murmelt. Es ist an der Zeit, mit ihm in den Wald kämpfen zu gehen.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (17, '`!K`9hsh`3at`!hr`3a V`9air`!iya`0', '....noch nicht von Merrick gefangen...', 'Mod-Biester', 'a:8:{s:4:"name";s:33:"`!K`9hsh`3at`!hr`3a V`9air`!iya`0";s:7:"wearoff";s:42:"`wDein Drache zieht sich erschöpft zurück!";s:9:"effectmsg";s:71:"`wDein Drache trifft den Gegner und verursacht {damage} Schadenspunkte!";s:6:"rounds";s:3:"200";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"22";s:15:"maxbadguydamage";s:2:"30";s:8:"activate";s:7:"offense";}', 75, 12000, 0, 0, 1, '`wDein Drache ist hungrig!', '', '', 0, 0, 0, '', '', '', 255, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (18, '`9Sternendrache`0', '', 'Mod-Biester', 'a:13:{s:4:"name";s:17:"`9Sternendrache`0";s:8:"roundmsg";s:101:"`9Alles scheint in eine unnatürliche Leere gehüllt zu sein, als sich der Sternendrache materialisiert";s:7:"wearoff";s:53:"`9Der Schattendrache fliegt davon, um sich auszuruhen";s:9:"effectmsg";s:96:"`9Der Schatten an Deiner Seite kämpft mit Dir. Du siehst die Furcht in den Augen Deines Feindes!";s:6:"rounds";s:2:"90";s:6:"atkmod";s:3:"1.8";s:6:"defmod";s:3:"1.8";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"50";s:12:"damageshield";s:3:"0.2";s:12:"badguyatkmod";s:3:"0.7";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 50000, 0, 0, 0, 1, '`9Aus den Weiten senkt sich ein elyrischer Schatten zu Dir hernieder. Dein Sternendrache ist zu Dir zurückgekehrt!', '`9Dein dunkler Gefährte ist zufrieden. Ein eiskalter Schauer fährt über Deinen Rücken und lässt Deine Haare zu Berge stehen - Wenn Du nicht wüsstest, dass es ein Zeichen größter Dankbarkeit ist würdest Du am liebsten schreiend davon rennen', '`9Dein Sternendrache ist zufrieden', 30, 50, 20, '', '', '', 200, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (19, '`)Ishtarin`0', 'Ein fleisch gewordener Albtraum und zum Glück eine Seltenheit in ganz Lumenor. Diese prähistorischen Raptoren gleichenden zweibeinigen Wesen sind die perfekten Jäger und Killer. Egal ob in sengender Wüste, dichten Urwäldern oder schlammigen Sümpfen. Ob mit oder ohne Reiter, nirgendwo gibt es ein Tier, dass es in Sachen Schnelligkeit und Gewandheit mit ihnen aufnehmen kann. Woher Ibga dieses Exemplar hat ist nicht bekannt, aber es wird gemunkelt das er es in Pakten mit einigen mächtigen Erzdämonen erstanden hat, denn eigentlich sollten die Ishtarin längst ausgestorben sein. Angeblich sollen in den Hallen NODs auch Versuche unternommen werden diese Tiere zu züchten, um damit eine überlegene Reitertruppe aufzustellen.', 'Mod-Biester', 'a:10:{s:4:"name";s:12:"`)Ishtarin`0";s:8:"roundmsg";s:31:"`wDein Gegner hat keine Chance.";s:9:"effectmsg";s:38:"Du regenerierst {damage} Lebenspunkte.";s:6:"rounds";s:3:"999";s:6:"atkmod";s:3:"1.2";s:6:"defmod";s:3:"1.1";s:7:"lifetap";s:2:"10";s:12:"badguyatkmod";s:4:"0.05";s:12:"badguydefmod";s:4:"0.05";s:8:"activate";s:10:"roundstart";}', 65535, 16777215, 0, 4, 1, '`wDein Ishtarin ist hungrig!', '', '', 0, 0, 0, '', '', '', 255, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (20, '`1C`!e`9r`3a`#p`&t`!e`1r`0', 'Kaum größer als ein einfaches Pony ist ein Cerapter der ideale Ersatz für einen Pegasus, wenn der Besitzer zu klein für ein solch großes und edles Tier ist. Gutmütig und stets friedlich ist es der hervorragende Gefährte für Abenteurer mit schwachen Nerven, aber auch für Zwerge sind sie gut geeignet, da die Größe hier gut angepasst ist. Es sollte jedoch auch berücksichtigt werden, das auch Cerapter gerne einen Dickkopf haben; wie viele Ponys.', 'Reittiere', 'a:8:{s:4:"name";s:26:"`1C`!e`9r`3a`#p`&t`!e`1r`0";s:9:"effectmsg";s:78:"`wDurch die reine Aura deines Cerapters regenerierst du {damage} Lebenspunkte!";s:6:"rounds";s:3:"160";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"1.1";s:5:"regen";s:1:"3";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 75, 5000, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 10, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (21, '`TK`ta`&t`Tz`te`0', 'Alle kennen sie, alle lieben sie. Das treue Haustier, das beständig um die Beine streift und quälend miaut, immer auf das Essen erpicht, was der Besitzer grad zu sich nimmt. Alle Variationen, von fleckig über gestreift bis hin zu einfarbig, sind bei der Katze vertreten. Sie reicht kaum bis über die Hälfte der Wade und dennoch hat das intelligente Haustier den Besitzer schnell eingelullt. Eine Katze ist ein Muss für die, die ein Problem mit Mäusen haben, oder es erst gar nicht kriegen wollen.', 'Haustiere', 'a:8:{s:4:"name";s:17:"`TK`ta`&t`Tz`te`0";s:7:"wearoff";s:56:"`wDeine Katze zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:64:"`wDeine Katze trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"25";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:1:"4";s:8:"activate";s:7:"offense";}', 0, 150, 1, 0, 0, '`wMit einem erwartungsvollen Schnurren streicht deine Katze um deine Beine herum.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (22, '`$T`4hestral`0', 'Hast du den Tod gesehen? So siehe dem Thestral ins Auge.', 'Mod-Biester', 'a:11:{s:4:"name";s:14:"`$T`4hestral`0";s:8:"roundmsg";s:40:"`4Dein Thestral umkreist dich schützend.";s:7:"wearoff";s:47:"`4Dein Thestral verschwindet in den Schatten.`0";s:9:"effectmsg";s:67:"`4Dein Thestral berührt dich, worauf deine Wunden sich schließen.`0";s:13:"effectfailmsg";s:71:"`4Dein Thestral stellt sich schützend vor dich und spannt die Flügel.`0";s:6:"rounds";s:3:"300";s:6:"atkmod";s:3:"1.6";s:6:"defmod";s:3:"1.6";s:5:"regen";s:1:"6";s:7:"lifetap";s:1:"1";s:8:"activate";s:26:"roundstart,offense,defense";}', 2000, 300000, 0, 3, 1, '`4Dein Thestral versteckt sich in den Schatten deines Anwesens, funkelt dich mit seinen roten Augen an und wartet still und unbemerkt.', 'Dein Thestral verschwindet und kommt kurz darauf gestärkt wieder.', '', 100, 0, 75, '`4Dein Thestral flog dich vor die Miene, was sich wohl ausser Gold darin befindet?`0', '`4Dein Thestral versuchte dich zu retten, erfolglos. Es verschwindet zurück in die Schatten.`0', '`4Dein Thestral hat sich für dich geopfert und dich gerettet.`0', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (23, '`THi`6p`^p`&og`^ry`6p`Th`0', 'Im ersten Augenblick denkt man, einen Greifen vor sich zu haben, aber kaum ist der Moment vorüber, fallen die Unterschiede auf. Vogelähnliche Klauen bilden seine Vorderbeine, während Hufen und Pferdebeine seine Hinterhand bilden. Der Schweif glänzt verführerisch ordentlich, so wie der Rest des weichen Felles. Sehr abschätzend wirkt die Kreatur, die jeden aus ihren Falkenaugen heraus genau anblickt und stets ruhig mit den Flügeln, die denen der Falken ähneln, raschelt.', 'Reittiere', 'a:6:{s:4:"name";s:26:"`THi`6p`^p`&og`^ry`6p`Th`0";s:6:"rounds";s:3:"120";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"1.1";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 40, 2500, 1, 1, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (24, '`7W`&o`)l`&f`0', 'Text folgt noch.', 'Wildtiere', 'a:8:{s:4:"name";s:14:"`7W`&o`)l`&f`0";s:7:"wearoff";s:54:"`wDein Wolf zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:62:"`wDein Wolf trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"80";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"4";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 0, 4500, 1, 0, 0, '`wMit funkelnden Augen sieht dir dein Wolf schon von weitem entgegen. Er scheint ungeduldig und hungrig zu sein.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (25, '`7R`ea`pb`8e`0', 'Text folgt noch.', 'Wildtiere', 'a:8:{s:4:"name";s:14:"`7R`ea`pb`8e`0";s:7:"wearoff";s:54:"`wDein Rabe zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:62:"`wDein Rabe trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"0";s:15:"maxbadguydamage";s:1:"4";s:8:"activate";s:7:"offense";}', 0, 600, 1, 0, 0, '`wDein Rabe hockt auf einem Ast vor deinem Fenster und starrt dich an.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (27, '`&S`)il`7ber`estu`ste', '`7 Eine riesige Schlange..man sollte ihr nicht zu nah kommen wenn sie Hunger hat..', 'Mod-Biester', 'a:14:{s:4:"name";s:18:"`2Ba`@si`gli`&sk`0";s:8:"roundmsg";s:69:"`7 Dein `2Ba`@si`gli`&sk`7 schlängelt sich von deinen Schultern hinab";s:7:"wearoff";s:57:"`7 Dein`2Ba`@si`gli`&sk `7 rollt sich erschöpft zusammen.";s:9:"effectmsg";s:89:"`7 Dein `2Ba`@si`gli`&sk `7 beißt dir in den Knöchel...sein Gift scheint dich zu heilen..";s:13:"effectfailmsg";s:65:"`7 Dein `2Ba`@si`gli`&sk `7 zischt deinen Gegner bedrohlich an...";s:6:"rounds";s:3:"180";s:6:"atkmod";s:3:"1.5";s:6:"defmod";s:3:"1.3";s:5:"regen";s:3:"1.1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"30";s:7:"lifetap";s:3:"0.5";s:12:"badguyatkmod";s:3:"0.8";s:8:"activate";s:26:"roundstart,offense,defense";}', 200, 300000, 0, 3, 1, '`7 Dein `2Ba`@si`gli`&sk `7 lässt sich eifrig zischend auf deinen Schultern nieder', '`7 Dein `2Ba`@si`gli`&sk `7 verspeist einige Bauernlümmel', '', 100, 25, 75, '`7 Dein `2Ba`@si`gli`&sk `7 schlängelt sich zu einer Miene..', '`7 Dein `2Ba`@si`gli`&sk`7 ist jetzt nur noch als schöner Gürtel verwendbar.', '`7 Dein `2Ba`@si`gli`&sk`7 hat dich gerettet udn sein Leben für deins gegeben..was für ein tapferes Ding...', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (28, '`@F`2ee`knd`grac`2h`@e`0', 'Eine sehr seltene, in den verschiedensten Grüntönen schimmernde Kreatur aus den Elfenreichen im Westen. Feendrachen werden nicht einmal 2 Meter lang und anstatt der üblichen Schwingen besitzen sie Flügel, die denen einer übergroßen Libelle gleichen. Sie sind weder gute Kämpfer, noch besitzen sie einen magischen Odem, doch munkelt man sie hätten einen regenerativen Einfluss auf ihre Umgebung.', 'Drachlinge', 'a:10:{s:4:"name";s:24:"`@F`2ee`knd`grac`2h`@e`0";s:8:"roundmsg";s:62:"`wDu spürst die alles regenerierende Aura deines Feendrachens!";s:7:"wearoff";s:60:"`wDein Feendrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:68:"`wDein Feendrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"150";s:5:"regen";s:1:"2";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"10";s:8:"activate";s:7:"offense";}', 50, 25000, 1, 0, 0, '`wDein Feendrache schwirrt ungeduldig durch die Baumkronen im Wald. Anscheinend kann er es nicht abwarten, bis du ihm folgst, um gemeinsam zu kämpfen.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (29, '`$Va`4m`$pi`4rfle`$de`4rma`$us`0', 'Anders als normale Fledermäuse ist diese Fledermaus für jedermann gefährlich, da sie auch Menschen, Elfen oder ähnliches attackiert. Ganz wie ihre Namensvetter saugt die Vampirfledermaus ihren Opfern einiges an Blut aus, um zu überleben. Insbesondere Vampire wünschen die Gegenwart der pelzigen Fledermäuse mit ihren Leder ähnlichen Flügeln und ihrer graubraunen Farbe.', 'Magische Wesen', 'a:7:{s:4:"name";s:32:"`$Va`4m`$pi`4rfle`$de`4rma`$us`0";s:7:"wearoff";s:28:"`wDeine Fledermaus ist satt!";s:9:"effectmsg";s:63:"`wDank deiner Fledermaus regenerierst du {damage} Lebenspunkte!";s:6:"rounds";s:2:"60";s:11:"minioncount";s:1:"1";s:7:"lifetap";s:1:"1";s:8:"activate";s:10:"roundstart";}', 25, 1000, 1, 0, 0, '`wKurz nachdem die Nacht hereingebrochen ist, öffnet deine Fledermaus schlagartig ihre wachsamen Augen. Es ist nicht schwer zu erkennen, dass es sie nach Blut dürstet.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (30, '`1H`9a`&rp`9y`1e`0', 'Fast wunderschön ist die Frau anzusehen mit den Federschwingen, doch bei genauerem Hinsehen fallen die groben Unterschiede auf: Die prachtvollen Flügel sind fest mit den gelenken Armen verwachsen, die Füße sind nicht mehr als Vogelklauen. Eine unterhaltsame Begleitung für jeden Mann und auch als Kampfgefährtin wirklich nützlich.', 'Magische Wesen', 'a:9:{s:4:"name";s:18:"`1H`9a`&rp`9y`1e`0";s:7:"wearoff";s:57:"`wDeine Harpye zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:65:"`wDeine Harpye trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"80";s:6:"defmod";s:4:"0.95";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 15, 1000, 1, 0, 0, '`wDie Flügel der Kreatur umspielen dich sanft, doch sobald du den hinterlistigen Gesichtsausdruck siehst, merkst du, dass deine Harpye nur endlich auf Jagd gehen will.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (31, '`TGolem`0', 'Text folgt noch.', 'Magische Wesen', 'a:8:{s:4:"name";s:9:"`TGolem`0";s:7:"wearoff";s:29:"`wDein Golem zerfällt wieder!";s:9:"effectmsg";s:60:"`wDein Golem trifft den Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"80";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"35";s:8:"activate";s:7:"offense";}', 30, 2500, 1, -1, 0, '`wDu entdeckst deinen Golem hinter dem Haus, wo er sich einfach hingehockt hat und wohl die ganze Nacht so unsinnig in die Luft gestarrt hat, um auf dich zu warten.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (32, '`^Ska`6r`&ab`6ä`^us`0', 'Text folgt noch.', 'Magische Wesen', 'a:7:{s:4:"name";s:21:"`^Ska`6r`&ab`6ä`^us`0";s:7:"wearoff";s:44:"`wDie Kraft deines Skarabäus ist verbraucht!";s:9:"effectmsg";s:83:"`wDie magische Aura deines Skarabäus lässt dich {damage} Lebenspunkte regenerieren!";s:6:"rounds";s:2:"40";s:5:"regen";s:1:"5";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 30, 0, 1, -1, 0, '`wKaum ist dein Skarabäus munter, hast du schon alle Mühe ihn in der Hand zu halten und vor allem vor neidischen Blicken zu schützen, denn der kleine Käfer krabbelt aufgeweckt umher.', '', '', 100, 0, 100, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (33, '`QG`qo`tl`&d`tf`qi`Qsch`0', 'Eines der wahrscheinlich am leichtesten sauber zu haltenden Tiere, die ein Abenteurer sich wünschen kann. Ein Goldfisch ist nicht nur nett anzusehen - nein! - er ist auch der ideale Gefährte für einen Abenteurer, der nicht sehr redselig ist, aber auch nicht alleine sein will. Außer einem gelegentlichen Blubb wird er von dem Goldfisch nichts zu hören bekommen, geschweige denn Unterschützung bekommen.', 'Haustiere', 'a:5:{s:4:"name";s:25:"`QG`qo`tl`&d`tf`qi`Qsch`0";s:8:"roundmsg";s:26:"`wDein Goldfisch schwimmt!";s:6:"rounds";s:2:"42";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 0, 5, 1, 0, 0, '`wDein Goldfisch schwimmt einige Runden in seinem Glas und blubbert vor sich her.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (34, '`TSc`7hw`&ar`7ze`Tr Pa`7n`&th`7e`Tr`0', 'Text folgt noch.', 'Wildtiere', 'a:8:{s:4:"name";s:37:"`TSc`7hw`&ar`7ze`Tr Pa`7n`&th`7e`Tr`0";s:7:"wearoff";s:67:"`wDein schwarzer Panther zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:76:"`wDein schwarzer Panther trifft deinen Gegner mit {damage} Schadenspunkten! ";s:6:"rounds";s:3:"120";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"15";s:8:"activate";s:7:"offense";}', 12, 5000, 1, 0, 0, '`wDein schwarzer Panther streift ungeduldig durch den Wald. Er scheint bereits auf dich zu warten.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (35, '`4Erz`Qtro`^ll`0', '`2Der gewöhnliche `4Erz`Qtro`^ll`2 ist eine äußerst furchterregende Erscheinung.', 'Mod-Biester', 'a:14:{s:4:"name";s:8:"Erztroll";s:8:"roundmsg";s:61:"`2Keine Angst, dein `4Erz`Qtro`^ll`2 steht direkt hinter dir.";s:7:"wearoff";s:53:"`2Dein `4Erz`Qtro`^ll`2 verschwindet in seiner Höhle.";s:9:"effectmsg";s:72:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule und trifft den Gegner schwer";s:14:"effectnodmgmsg";s:70:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule.. in die falsche Richtung.";s:13:"effectfailmsg";s:70:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule.. in die falsche Richtung.";s:6:"rounds";s:3:"120";s:6:"atkmod";s:3:"1.2";s:6:"defmod";s:3:"1.1";s:5:"regen";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"10";s:7:"lifetap";s:1:"1";s:8:"activate";s:15:"offense,defense";}', 10000, 75, 0, 0, 1, '`2Dein `4Erz`Qtro`^ll`2 gibt ein Rülpsen von sich und tapst hinter dir her.', '`2Dein `4Erz`Qtro`^ll`2 reibt sich zufrieden den gigantischen Bauch.', '`2Dein `4Erz`Qtro`^ll`2 hat den halben Wald von Rehen befreit, ist aber immer noch nicht satt.', 100, 0, 100, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (36, '`6Re`^nn`tsch`^we`6in`0', 'Text folgt noch.', 'Reittiere', 'a:7:{s:4:"name";s:23:"`6Re`^nn`tsch`^we`6in`0";s:6:"rounds";s:2:"60";s:6:"atkmod";s:3:"0.9";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.75";s:8:"activate";s:10:"roundstart";}', 12, 500, 1, 3, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (37, '`TB`2au`@md`gra`@c`2h`Te`0', 'Baumdrachen sind eine kleine und weit weniger intelligente Version der großen grünen Drachen. Ihr Feueratem ist nicht wirklich beeindruckend, aber auf kurze Distanz dennoch sehr schmerzhaft. Baumdrachen erreichen eine Spannweit von bis zu 3 Metern.', 'Drachlinge', 'a:8:{s:4:"name";s:26:"`TB`2au`@md`gra`@c`2h`Te`0";s:7:"wearoff";s:60:"`wDein Baumdrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:68:"`wDein Baumdrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"90";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"15";s:8:"activate";s:7:"offense";}', 20, 2500, 1, 0, 0, '`wDein Baumdrache fliegt vom Wald her zu dir herüber. Hat er vielleicht versucht, den grünen Drachen ohne dich zu finden?', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (38, '`fPe`erl`sd`frac`eh`se`0', 'Wahrscheinlich einer der elegantesten Drachen im ganzen Kaiserreich. Ihr Körper erreicht eine Länge von annähernd 5 Metern und ist beinahe so schlank wie der einer Wyver. In ihrer Heimat an den westlichen Küsten, stürzen sie sich aus großer Höhe in die Fluten um ihre Lieblingsnahrung, frischen Fisch zu jagen, wobei sie sich unter Wasser nicht minder elegant bewegen, wie in der Luft.', 'Drachlinge', 'a:8:{s:4:"name";s:24:"`fPe`erl`sd`frac`eh`se`0";s:7:"wearoff";s:60:"`wDein Perldrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:68:"`wDein Perldrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"120";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 30, 5000, 1, 0, 0, '`wDein Perldrache liegt entspannt auf der Wiese und blickt dich erwartungsvoll an, um vielleicht einen Fisch von dir zu bekommen.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (39, '`3Ei`#sd`&ra`#c`3he`0', 'Eigentlich mehr ein Drachling und kein voll ausgewachsener Drache. Der eisige Atem dieser bis zu 5 Meter langen, silbrig schimmernden Kreaturen ist aber dennoch absolut tödlich für all jene, welche sich zu nahe an sie heran wagen.', 'Drachlinge', 'a:8:{s:4:"name";s:21:"`3Ei`#sd`&ra`#c`3he`0";s:7:"wearoff";s:59:"`wDein Eisdrache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:67:"`wDein Eisdrache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"160";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 75, 10000, 1, 0, 0, '`wDein Drache hat es sich im dunkelsten und kühlsten Abschnitt des Waldes gemütlich gemacht, wo er sich die Zeit, bis du kommst, mit kleinen Eisspielchen vertreibt.', '', '', 0, 0, 0, '', '', '', 15, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (40, '`4R`$o`&t`$e`4r D`$r`&ac`$h`4e`0', 'Ebenfalls nur ein Drachling. Eine kleinere Version ihrer großen Verwandten aus dem fernen Süden, deren feuriger Atem ganze Landstriche verheeren konnte. Diese Exemplare hier, sind mit ihren 5-6 Metern Spannweite noch lange nicht ausgewachsen, doch kann ihr Atem dennoch jeden Gegner ohne große Probleme garen lassen.', 'Drachlinge', 'a:8:{s:4:"name";s:32:"`4R`$o`&t`$e`4r D`$r`&ac`$h`4e`0";s:7:"wearoff";s:62:"`wDein Roter Drache zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:70:"`wDein Roter Drache trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"180";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 75, 10000, 1, 0, 0, '`wDein roter Drache kreist schon lange über dir, in der Hoffnung endlich jagen zu gehen.', '', '', 0, 0, 0, '', '', '', 15, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (41, '`6G`Vhu`6l`0', 'Jene unter euch, denen Ästhetik eher weniger wichtig ist, mögen den Ghul einem Skelettkrieger vorziehen. Sie sind ausdauernder und kräftiger, doch ist der stinkende und permanent verwesende, ehemals humanoide Körper nicht für jeden zu ertragen.', 'Geister und Dämonen', 'a:10:{s:4:"name";s:12:"`6G`Vhu`6l`0";s:7:"wearoff";s:54:"`wDein Ghul zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:62:"`wDein Ghul trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:1:"8";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 0, 2200, 1, -1, 0, '`wAls du den Gestank von Verwesung riechst und eine Schleimspur vor deinem Haus entdeckst, weißt du, dass dein Ghul nicht weit sein kann.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (42, '`5S`%u`&cc`%u`5b`&u`%s`0', 'Gebt auf euch Acht, solltet ihr euch wahrhaftig eines dieser verderbten und diabolischen Wesen erwerben wollen. Sie sind zwar sehr gute und ausdauernde Kämpfer, doch soll schon so mancher Möchtegern-Abenteurer ihnen in tiefer Nacht erlegen sein. Nicht ihren schaften Klingen, sondern ihrer unbeschreiblichen Schönheit. Letztenendes, führt aber beides bei Succubi zum Tode.', 'Geister und Dämonen', 'a:10:{s:4:"name";s:24:"`5S`%u`&cc`%u`5b`&u`%s`0";s:7:"wearoff";s:58:"`wDein Succubus zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:66:"`wDein Succubus trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"120";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"15";s:12:"badguyatkmod";s:3:"0.8";s:8:"activate";s:7:"offense";}', 40, 10000, 1, 0, 0, '`wEine Weile suchst du deinen Succubus, bis du ihn im Nebenzimmer mit einem schlafenden Mann findest. Allerdings musst du ihn erst davon überzeugen, nun mit dir jagen zu gehen.', '', '', 0, 0, 0, '', '', '', 10, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (43, '`1K`9o`&bo`4l`$d`0', 'Ein Exemplar auf das ich besonders stolz bin. Kobolde sind noch etwas kleiner aus Goblins und werden vielleicht einen halben Meter groß. Aber diese hier, sind nicht nur einfache Kobolde, sondern ihre magisch begabten Verwandten aus den Unterwelten. Sie können eure Gegner verfluchen und selbst aus geschickten Kriegern einen Tollpatsch machen.', 'Geister und Dämonen', 'a:8:{s:4:"name";s:18:"`1K`9o`&bo`4l`$d`0";s:8:"roundmsg";s:53:"`wLachend und kichernd wirkt dein Kobold seine Magie!";s:7:"wearoff";s:41:"`wPlötzlich ist dein Kobold verschwunden!";s:6:"rounds";s:2:"20";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.5";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 20, 1000, 1, 0, 0, '`wNeben dem Bett sitzt dein Kobold und sieht zu dir hoch. An seinem Blick kannst du erkennen, dass er schon wieder etwas ausgeheckt hat.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (44, '`RP`ri`&x`ri`re`0', 'Auch oft Waldfee genannt. Handgroße, humanoid erscheinende Kreaturen mit libellenartigen Flügeln. Sie sind weder gefährlich, noch in irgendeiner Weise aggressiv. Merricks Exemplare können zwar nicht sprechen, doch besitzen sie die gleichen magischen Fähigkeiten und die gleiche, alles regenerierende Aura, wie ihre Feenbrüder und Schwestern aus den Wäldern um Atrahor.', 'Magische Wesen', 'a:8:{s:4:"name";s:17:"`RP`ri`&x`ri`re`0";s:8:"roundmsg";s:42:"`wDeine Pixie wirkt ihre heilenden Zauber!";s:7:"wearoff";s:42:"`wDeine Pixie zieht sich erschöpft zurück!";s:9:"effectmsg";s:79:"`wDie magische Aura deiner Pixie lässt dich {damage} Lebenspunkte regenerieren!";s:6:"rounds";s:2:"80";s:5:"regen";s:1:"1";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 30, 5000, 1, 1, 0, '`wKaum bist du munter, schwirrt auch schon deine Pixie aufgeregt um dich herum und bereitet dir dadurch noch mehr Kopfschmerzen. Sie scheint ungeduldig auf neue Erlebnisse zu warten.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (45, '`1T`!od`1e`!sf`1ee`0', 'Die Banshee, oder auch Todesfee, tritt in Form einer alten, verwelkten Elfe mit eisig blasser Haut in Erscheinung. Beständig  scheint sie zu jammern und zu klagen, aber erst bei der Gefahr eines nahenden Gegners beginnt sie ihr wahres, Mark und Bein durchdringendes Klagelied, welches selbst gestandenen Helden den Mut auf alle Hoffnung zu nehmen vermag.', 'Geister und Dämonen', 'a:10:{s:4:"name";s:20:"`1T`!od`1e`!sf`1ee`0";s:8:"roundmsg";s:78:"`wDeine Todesfee singt ihr grausames, Mark und Bein durchdringendes Klagelied!";s:7:"wearoff";s:58:"`wTheatralisch lässt sich deine Todesfee zu Boden gleiten!";s:6:"rounds";s:2:"30";s:6:"atkmod";s:4:"0.95";s:6:"defmod";s:4:"0.95";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.5";s:12:"badguydefmod";s:4:"0.95";s:8:"activate";s:10:"roundstart";}', 35, 7500, 1, 0, 0, '`wVom Wald her hörst du wehleidiges Jammern und Klagen. Sofort weißt du, dass deine Todesfee sich schon ohne dich auf den Weg gemacht hat.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (46, '`6Kr`aö`8te`0', 'Warzige kleine Viecher, die Merrick einem örtlichen Hexenzirkel abschwatzen konnte. Viel gibt es zu diesen faustgroßen Tieren nicht zu sagen. Haltet sie stets feucht, denn ihre Sekrete verursachen nicht nur starken Juckreiz, sondern vermögen auch kleinere Wunden zu heilen.', 'Haustiere', 'a:6:{s:4:"name";s:13:"`6Kr`aö`8te`0";s:8:"roundmsg";s:73:"`wDu ignorierst das Jucken und verschmierst die Sekrete auf deine Wunden!";s:7:"wearoff";s:44:"`wDeine Kröte braucht dringend etwas Wasser!";s:6:"rounds";s:2:"10";s:5:"regen";s:1:"1";s:8:"activate";s:10:"roundstart";}', 1, 50, 1, 0, 0, '`wVom Teich vor deinem Haus hörst du schon deine Kröte quaken.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (47, '`$P`qa`^p`$a`^g`qe`$i`0', 'Ein bunter Vogel mit großem Schnabel...leider aber auch einem großen Mundwerk. Früher einmal von Piraten an den westlichen Küsten gehalten, kennt dieses Tier Schimpfworte und Begriffe, die noch jedem Schamesröte ins Gesicht treiben konnten. Merrick wird dir einen besonders guten Preis machen müssen...', 'Haustiere', 'a:7:{s:4:"name";s:23:"`$P`qa`^p`$a`^g`qe`$i`0";s:8:"roundmsg";s:78:"`wDein Papagei beleidigt deinen Gegner so lange, bis dieser anfängt zu weinen!";s:7:"wearoff";s:33:"`wDein Papagei hält den Schnabel!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.85";s:8:"activate";s:10:"roundstart";}', 5, 500, 1, 0, 0, '`wAuf einer Stange vor dir sitzt dein Papagei und krächzt immer wieder deinen Namen.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (48, '`TF`tr`Te`ttt`Tch`te`Tn`0', 'Klein, niedlich und hübsch anzusehen, liegen die Vorteile des Frettchens in seiner Statur. Es gibt keine Rüstung welche derart lückenlos ist, als dass nicht ein Frettchen noch hinein schlüpfen und einen wunden Punkt finden könnte...und ihre Bisse sind alles andere als angenehm.', 'Haustiere', 'a:8:{s:4:"name";s:25:"`TF`tr`Te`ttt`Tch`te`Tn`0";s:7:"wearoff";s:59:"`wDein Frettchen zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:67:"`wDein Frettchen trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"30";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"4";s:8:"activate";s:7:"offense";}', 0, 200, 1, 0, 0, '`wDein Frettchen beißt dir auffordernd in die Wade.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (49, '`7E`&s`7e`&l`0', 'Ein störrisches und nicht besonders flinkes Ding. Aber immerhin lässt es sich (zumindest manchmal) reiten und ist zudem noch ausgesprochen günstig. Genau das Richtige für den weniger betuchten Abenteurer.', 'Reittiere', 'a:5:{s:4:"name";s:14:"`7E`&s`7e`&l`0";s:6:"rounds";s:2:"60";s:6:"atkmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 5, 10, 1, 0, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (50, '`)S`7ke`slet`at-Ka`fmp`sfr`7os`)s`0', 'Einigen Totenbeschwörern ist es endlich gelungen, in Schlachten gefallene Schlachtrösser neu zu beseelen. Zwar sind sie weniger stark als ihre Lebenden Vorgänger, doch erfüllt der Anblick ihrer blanken Knochen so manchen Gegner mit Schrecken.', 'Reittiere', 'a:7:{s:4:"name";s:35:"`)S`7ke`slet`at-Ka`fmp`sfr`7os`)s`0";s:6:"rounds";s:3:"220";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:3:"1.1";s:15:"maxbadguydamage";s:3:"1.1";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 50, 1000, 1, 1, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (51, '`lRi`mes`)en`lspi`mnn`)e`0', 'Die großen Waldspinnen der östlichen Wälder. Ihr Biss ist nicht sehr giftig, weshalb es einigen Goblinstämmen gelungen ist diese 4-5 Meter großen Kreaturen zu zähmen. Haltet euch gut im Sattel fest, denn diese Biester können auch problemlos an Wänden oder Decken entlang laufen.', 'Reittiere', 'a:7:{s:4:"name";s:26:"`lRi`mes`)en`lspi`mnn`)e`0";s:6:"rounds";s:3:"120";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:4:"1.15";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 55, 2500, 1, 2, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 10, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (52, '`2Re`kit`@eide`kch`2se`0', 'Eigentlich ein nachtaktives und eher harmloses Tier von etwa 5 Metern Länge, dass sich von kleineren Säugern  und Amphibien ernährt. Im Kampf sollte man diese ungewöhnlich schnellen Kreaturen allerdings nicht unterschätzen. Ihre mit Saugnäpfen versehenen Füße lassen sie sich auf jedem Untergrund und sogar an Wänden und Decken gleichermaßen behende bewegen.', 'Reittiere', 'a:7:{s:4:"name";s:24:"`2Re`kit`@eide`kch`2se`0";s:6:"rounds";s:3:"150";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:3:"1.1";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.95";s:8:"activate";s:10:"roundstart";}', 40, 800, 1, 3, 1, '`wDu steckst dein(e/n) {weapon}`w in die Satteltasche deines Tieres und machst dich auf ins Abenteuer!', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (53, '`)S`&t`7e`&in`T-`)G`&a`7r`&go`7y`&le`0', 'Bei dieser Kreatur handelt es sich nicht nur um ein beeindruckendes Stück zwergischer Handwerkskunst, sondern auch um ein Meisterwerk der Beschwörungskunst. Zwar sind sie nicht intelligent und auch nicht besonders flink, aber dafür kommt einer ihrer Schläge schon gut dem Biss eines kleinen Drachen gleich.', 'Magische Wesen', 'a:8:{s:4:"name";s:38:"`)S`&t`7e`&in`T-`)G`&a`7r`&go`7y`&le`0";s:7:"wearoff";s:58:"`wDein Gargoyle zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:66:"`wDein Gargoyle trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"90";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"16";s:8:"activate";s:7:"offense";}', 10, 500, 1, 0, 0, '`wWie eine Statue hockt dein Stein-Gargoyle nahe des Hauses und erwacht erst zum Leben, als er das Wort ?Kampf? hört.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (54, '`4F`$e`Qu`qe`^r`q-`QG`$a`4rg`$o`Qy`ql`^e`0', 'Aus einem haselnussgroßen Rubin beschworen, handelt es sich hierbei um eine Kreatur aus reinstem Feuer, die ihre Gegner mit Feuerbällen, Flammenlanzen und anderen hitzigen Spielereien in die Knie zwingt. Aber Vorsicht! Schon so mancher Held hat sich selbst an diesen Biestern mehr als nur die Finger verbrannt.', 'Magische Wesen', 'a:11:{s:4:"name";s:42:"`4F`$e`Qu`qe`^r`q-`QG`$a`4rg`$o`Qy`ql`^e`0";s:8:"roundmsg";s:58:"`wDie flammende Aura deines Gargoyles versengt deine Haut!";s:7:"wearoff";s:65:"`wDein Gargoyle zerfällt und nur ein kleiner Rubin bleibt zurück!";s:9:"effectmsg";s:59:"`wDein x trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:6:"defmod";s:4:"0.95";s:5:"regen";s:2:"-1";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"15";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 10, 500, 1, 0, 0, '`wAls du von der Hitze geweckt wirst, wird dir sofort klar, dass dein Feuer-Gargoyle deine Umgebung bereits in Brand gesteckt hat und dass er dringend eine Beschäftigung benötigt.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (55, '`TDr`)i`7d`&er`0', 'Eine groteske und hässliche Kreatur, bestehend aus einem nackten Spinnenkörper, der mit Beinen einen Durchmesser von 3-4 Metern erreicht und an dessen Kopfende ein aufgedunsener und unwirklich erscheindender menschlicher Oberkörper aufragt. So abscheulich diese Kreatur aber anzusehen ist, so gefährlich ist sie auch im Kampf, denn sie vermag zwei Waffen in einem harmonischen und präzisen Einklag zu führen.', 'Magische Wesen', 'a:9:{s:4:"name";s:16:"`TDr`)i`7d`&er`0";s:7:"wearoff";s:56:"`wDein Drider zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:64:"`wDein Drider trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"120";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:2:"25";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 60, 7500, 1, 0, 0, '`wAngriffslustig schwingt der Drider seine Waffen, als er die ersten Anzeichen bemerkt, dass er bald kämpfen kann. Deshalb musst du schnell in Deckung gehen, um nicht selbst zu seinem Opfer zu werden.', '', '', 0, 0, 0, '', '', '', 10, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (56, '`9E`3u`#l`&e`0', 'Nur etwa 20-30cm messen diese nachtaktiven und eher harmlos erscheinenden Kreaturen. Bleibt abzuwarten was eure Gegner zu ihren ausdauernden und sturen Angriffen sagen werden...', 'Wildtiere', 'a:8:{s:4:"name";s:14:"`9E`3u`#l`&e`0";s:7:"wearoff";s:55:"`wDeine Eule zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:63:"`wDeine Eule trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"5";s:8:"activate";s:7:"offense";}', 0, 1200, 1, 0, 0, '`wHoch oben im Baum sitz deine Eule und fixiert geduldig ihre Beute.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (57, '`qK`to`eb`tr`qa`0', 'Unscheinbar in grau-braunen Farbtönen gehalten, ist dies eine der gefährlichsten Giftschlangen rund um Atrahor. Keine Rüstung ist lückenlos genug, um diese windige, bis zu einem halben Meter lange Kreatur abzuwehren. Ihr Gift selbst verursacht Krämpfe, Übelkeit und vorübergehende Sehschwäche. Also gebt Acht das ihr sie nicht reizt!', 'Wildtiere', 'a:9:{s:4:"name";s:17:"`qK`to`eb`tr`qa`0";s:8:"roundmsg";s:60:"`wDeine Kobra verschwindet unter der Rüstung deines Gegners!";s:7:"wearoff";s:56:"`wDeine Kobra zieht sich erschöpft aus dem Kampf zurück!";s:6:"rounds";s:1:"5";s:6:"defmod";s:3:"0.5";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.25";s:12:"badguydefmod";s:3:"0.5";s:8:"activate";s:10:"roundstart";}', 5, 100, 1, 0, 0, '`wDurch einen Biss tötet deine Kobra ein Beutetier und verschlingt es anschließend, bevor sie sich zu dir schlängelt.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (58, '`5A`%n`vac`&o`vn`%d`5a`0', 'Tödliche Stille herrscht um diese Schlange, die problemlos bis zu 9 Metern Größe erreicht. Deutlich zu unterscheiden von den anderen Schlangen ist sie durch ihre olivgrüne Färbung und dunklen Flecken, die wie Augen auf ihr nächstes Opfer stieren. Schneller als man es diesem Kriechtier zutraut, hat es schon zugebissen und ihr hilfloses Opfer vergiftet.', 'Wildtiere', 'a:9:{s:4:"name";s:24:"`5A`%n`vac`&o`vn`%d`5a`0";s:7:"wearoff";s:59:"`wDeine Anaconda zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:67:"`wDeine Anaconda trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"15";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"0";s:15:"maxbadguydamage";s:2:"30";s:8:"activate";s:7:"offense";}', 2, 2500, 1, 0, 0, '`wZischeln schlängelt sich deine Anakonda durch ihr Terrarium, wobei sie jede deiner Bewegungen verfolgt.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (59, '`4F`Qu`qc`th`&s`0', 'Der gemeine Rot- oder Waldfuchs. Klein, listig und flink. Gut zum Übermitteln von Botschaften (Fuchsen), aber, sofern ihr ihn gut behandelt, auch ein treuer Gefährte im Kampf.', 'Wildtiere', 'a:8:{s:4:"name";s:17:"`4F`Qu`qc`th`&s`0";s:7:"wearoff";s:55:"`wDein Fuchs zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:63:"`wDein Fuchs trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"60";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"5";s:8:"activate";s:7:"offense";}', 0, 1500, 1, 0, 0, '`wFür neue Abenteuer bereit, sitzt dein Fuchs schon vor seinem Bau und starrt hinüber zum Wald.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (60, '`TH`ty`7ä`&ne`0', 'Gefangen in den weiten Steppen der dunklen Lande, nördlich von Atrahor. Selbst mit guter Abrichtung sind sie noch immer feige und hinterhältig. Doch gerade diese Hinterhältigkeit macht sie zu so gefährlichen Gegnern und mit ihren starken und kräftigen Kiefer sind nicht zu unterschätzen.', 'Wildtiere', 'a:8:{s:4:"name";s:15:"`TH`ty`7ä`&ne`0";s:7:"wearoff";s:41:"`wDeine Hyäne zieht sich winselnd zurück!";s:9:"effectmsg";s:64:"`wDeine Hyäne trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"9";s:8:"activate";s:7:"offense";}', 0, 2000, 1, 0, 0, '`wAls du aus dem Haus trittst, bemerkst du deine Hyäne, wie sie sich gerade über die Überreste eines Vogels hermacht. Allerdings ist sie auch danach noch hungrig!', '', '', 0, 0, 0, '', '', '', 0, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (61, '`4Ja`Qg`qu`ta`&r`0', 'Zu einer der größten, aber dazu auch schnellsten Katze gehörend, ist der Jaguar der beste Partner für Jäger der freien Felder. Mit einer Gesamtlänge von bis zu 2 Metern zeugt diese große Raubkatze nicht nur von tödlicher Geduld und Anmut, sondern vielmehr, das sie schnell, effektiv und vor allem aber unberechenbar ist - nicht nur für Feinde. Wer einen Jaguar als Gefährten hat, muss sich darauf einstellen, das dieser, so wie jede Katze, seinen eigenen, sturen Willen besitzt und sich ungern herumkommandieren lässt. Im Übrigen: Jaguar sind keine guten Kuschelfreunde. Wenn sie sich schon mal auf ihren Gefährten legen, wird es bei ungefähr 50 Pfund Gewicht etwas erdrückend.', 'Wildtiere', 'a:8:{s:4:"name";s:18:"`4Ja`Qg`qu`ta`&r`0";s:7:"wearoff";s:56:"`wDein Jaguar zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:64:"`wDein Jaguar trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"4";s:15:"maxbadguydamage";s:2:"14";s:8:"activate";s:7:"offense";}', 6, 1000, 1, 0, 0, '`wRuhend liegt dein Jaguar vor deinen Füssen, nur darauf wartend, dass die Jagd beginnt.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (62, '`tT`qi`Qg`qe`tr`0', 'Text folgt noch.', 'Wildtiere', 'a:8:{s:4:"name";s:17:"`tT`qi`Qg`qe`tr`0";s:7:"wearoff";s:55:"`wDein Tiger zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:56:"`wDein Tiger trifft deinen mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"14";s:8:"activate";s:7:"offense";}', 8, 2000, 1, 0, 0, '`wDein Tiger schleicht, auf dich wartend, am Waldrand auf und ab. Schon längst hat er seine Beute erspäht.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (63, '`mBr`4a`Qun`4b`mär`0', 'Kraftvoll und gefährlich ist er, der massige Braunbär mit seinen riesigen Pranken. Eine Gefahr für jedes unachtsame Wesen, eine Freude für jeden, der ihn an seiner Seite weiß. Mit einer Größe von 3 Fuß ist er zwar nicht der größte, aber mit das schwerste Wildtiere. Wer sich nicht vor seinen ständig wechselnden Launen hütet und ihm regelmäßig Fisch zu fressen gibt, wird böse Überraschungen erleben.', 'Wildtiere', 'a:8:{s:4:"name";s:20:"`mBr`4a`Qun`4b`mär`0";s:7:"wearoff";s:58:"`wDein Braunbär zieht sich erschöpft aus dem Kampf zurück!";s:9:"effectmsg";s:66:"`wDein Braunbär trifft deinen Gegner mit {damage} Schadenspunkten!";s:6:"rounds";s:3:"100";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 10, 5000, 1, 0, 0, '`wUnruhig geht dein Bär hin und her. Er knurrt vor Hunger und Ungeduld, denn er wollte bald ein paar Fische im Fluss erhaschen.', '', '', 0, 0, 0, '', '', '', 5, 1.14);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (64, '`tD`^ru`qid`Qen`$ro`4s`ms`0', '... stures Unikat das nicht bei Merick erhältlich ist...', 'Mod-Biester', 'a:17:{s:4:"name";s:27:"`tD`^ru`qid`Qen`$ro`4s`ms`t";s:8:"roundmsg";s:58:"`tDein D`^ru`qid`Qen`$ro`4s`ms`t rennt deinen Gegner um.`0";s:7:"wearoff";s:95:"`tDein D`^ru`qid`Qen`$ro`4s`ms`t hat genug für heute und galoppiert nach Hause um zu fressen.`0";s:9:"effectmsg";s:77:"`tDein D`^ru`qid`Qen`$ro`4s`ms`t verleiht dir durch seine Magie mehr Kraft.`0";s:14:"effectnodmgmsg";s:26:"`tDu bist völlig gesund.`0";s:13:"effectfailmsg";s:59:"`tDein D`^ru`qid`Qen`$ro`4s`ms`t konnte dir nicht helfen.`0";s:6:"rounds";s:3:"350";s:6:"atkmod";s:3:"1.4";s:6:"defmod";s:3:"1.4";s:5:"regen";s:1:"4";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"20";s:7:"lifetap";s:1:"1";s:12:"badguydmgmod";s:3:"0.9";s:12:"badguyatkmod";s:3:"0.8";s:12:"badguydefmod";s:3:"0.8";s:8:"activate";s:26:"roundstart,offense,defense";}', 50000, 50000, 0, 3, 1, '`tDein D`^ru`qid`Qen`$ro`4s`ms`t erwartet dich bereits, bereit dich durch einen weiteren Tag zu begleiten.`0', '`tDein D`^ru`qid`Qen`$ro`4s`ms`t döst eine Weile im Schatten der Bäume und erholt sich vollständig.`0', '`tDein D`^ru`qid`Qen`$ro`4s`ms`t frisst von dem frischen Gras welches hier wächst und ist bereit dich noch etwas länger zu begleiten`0', 50, 0, 50, '`tDein D`^ru`qid`Qen`$ro`4s`ms`t bleibt am Eingang der Mine stehen. Es wird hier auf dich warten.`0', '`tDein D`^ru`qid`Qen`$ro`4s`ms`t weiß das du nicht wieder herauskommen wirst und galoppiert zurück zu seinem Stall.`0', '`tDein D`^ru`qid`Qen`$ro`4s`ms`t weiß das du nicht wieder herauskommen wirst und galoppiert zurück zu seinem Stall.`0', 80, 1);
REPLACE `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (65, '`VF`eel`7e`ss D`)ä`7m`eo`Vn`0', '', 'Mod-Biester', 'a:12:{s:4:"name";s:29:"`VF`eel`7e`ss D`)ä`7m`eo`Vn`0";s:8:"roundmsg";s:27:"`wDein Tier kämpft mit dir.";s:7:"wearoff";s:165:"`VS`)ir`Va`s keucht leise auf, die Anstrengungen der letzten Kämpfe fordern ihren Tribut. Erschöpft sie läuft zu dir zurück und wird nun nicht weiter kämpfen können.";s:9:"effectmsg";s:34:"`wDu heilst {damage} Lebenspunkte!";s:14:"effectnodmgmsg";s:98:"`VS`)ir`Va`s absorbiert die Lebensenergie deines Gegners und überträgt sie komplett auf Dich über.";s:13:"effectfailmsg";s:232:"`VS`)ir`Va`s versucht die Lebensenergie deines Gegners zu absorbieren, doch genau in diesem Moment läuft ihr eine Maus vor die Nase die ihre Aufmerksamkeit auf sich zieht. Tija pech gehabt, sie ist manchmal eben doch nur eine Katze.";s:6:"rounds";s:3:"100";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"1.1";s:11:"minioncount";s:1:"1";s:7:"lifetap";s:1:"1";s:8:"activate";s:10:"roundstart";}', 50000, 500000, 0, 0, 1, '`VS`)ir`Va`s schleckt dir mit der rauen Zunge übers Gesicht. Zeit zum Aufstehen!  Da du so liebevoll geweckt wurdest, kannst du den Tag mit einem Lächeln auf dem Gesicht beginnen lassen.', '', '', 0, 0, 0, '', '', '', 100, 1.14);


# petitions:
ALTER TABLE petitions
ADD   `p_for` varchar(100) collate latin1_german1_ci NOT NULL default '';

# pollresults:
ALTER TABLE pollresults 
ADD   `pollid` mediumint(8) unsigned NOT NULL default '0',
  KEY `pollid` (`pollid`);


# polls:

CREATE TABLE `polls` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `question` varchar(255) collate latin1_german1_ci NOT NULL default '',
  `description` varchar(255) collate latin1_german1_ci NOT NULL default '',
  `author` int(10) unsigned NOT NULL default '0',
  `postdate` datetime NOT NULL default '0000-00-00 00:00:00',
  `section` varchar(30) collate latin1_german1_ci NOT NULL default '',
  `expire` datetime NOT NULL default '0000-00-00 00:00:00',
  `option1` varchar(120) collate latin1_german1_ci NOT NULL default '',
  `option2` varchar(120) collate latin1_german1_ci NOT NULL default '',
  `option3` varchar(120) collate latin1_german1_ci NOT NULL default '',
  `option4` varchar(120) collate latin1_german1_ci NOT NULL default '',
  `option5` varchar(120) collate latin1_german1_ci NOT NULL default '',
  `option6` varchar(120) collate latin1_german1_ci NOT NULL default '',
  `closed` tinyint(3) unsigned NOT NULL default '0',
  PRIMARY KEY  (`id`),
  KEY `section` (`section`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_german1_ci COMMENT='Enthält Umfragen' AUTO_INCREMENT=1 ;


#races :

CREATE TABLE `races` (
  `id` char(3) collate latin1_german1_ci NOT NULL default '',
  `name` varchar(40) collate latin1_german1_ci NOT NULL default '',
  `colname` varchar(60) collate latin1_german1_ci NOT NULL default '',
  `name_plur` varchar(44) collate latin1_german1_ci NOT NULL default '',
  `colname_plur` varchar(64) collate latin1_german1_ci NOT NULL default '',
  `active` tinyint(1) unsigned NOT NULL default '0',
  `loc_desc` varchar(255) collate latin1_german1_ci NOT NULL default '',
  `chosen_msg` text collate latin1_german1_ci NOT NULL,
  `long_desc` text collate latin1_german1_ci NOT NULL,
  `raceroom` tinyint(1) unsigned NOT NULL default '0',
  `raceroom_name` varchar(80) collate latin1_german1_ci NOT NULL default '',
  `raceroom_nav` varchar(80) collate latin1_german1_ci NOT NULL default '',
  `raceroom_desc` text collate latin1_german1_ci NOT NULL,
  `raceroom_all` tinyint(1) unsigned NOT NULL default '0',
  `superuser` tinyint(1) unsigned NOT NULL default '0',
  `mindk` tinyint(3) unsigned NOT NULL default '0',
  `newday_msg` varchar(255) collate latin1_german1_ci NOT NULL default '',
  `boni` text collate latin1_german1_ci NOT NULL,
  `specboni` text collate latin1_german1_ci NOT NULL,
  PRIMARY KEY  (`id`),
  KEY `active` (`active`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_german1_ci COMMENT='Speichert Rassen.';

-- 
-- Daten für Tabelle `races`
-- 

INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('men', 'Mensch', 'Mensch', 'Menschen', 'Menschen', 1, '', '`&Deine Größe und Stärke als Mensch erlaubt es dir, Waffen ohne große Anstrengungen zu führen und dadurch länger durchzuhalten, als andere Rassen.`n`^Du hast jeden Tag einen zusätzlichen Waldkampf!', 'Du wurdest im Flachland in der Stadt Romar, der Stadt der `&Menschen`0, geboren. Du hast immer nur zu deinem Vater aufgesehen und bist jedem seiner Schritte gefolgt, bis er auszog den `@Grünen Drachen`0 zu vernichten und nie wieder gesehen wurde.', 2, 'Versammlungsraum', 'In den Versammlungsraum', '`tDu gehst schnellen Schrittes durch die Strassen, in der Hoffnung dieses eine Haus wieder zu finden. Hier sehen alle Häuser gleich aus, und wüßtest du nicht wo du hin musst, wäre es dir unmöglich dich hier zurecht zu finden. Dann plötzlich stehst du vor einem Gutshaus, von dem du glaubst, dass es das Richtige ist. Du schlägst den wuchtigen Türklopfer gegen die beschlagene Eichtür - zweimal kurz, einmal lang, dreimal kurz. Eine kleine Klappe schiebt sich auf und ein Augenpaar mustert dich. Dann wird die Tür geöffnet und du wirst steinerne Stufen in ein großes Gewölbe geführt. Hier haben sich Menschen zusammen gefunden. Sie essen, trinken, amüsieren sich. Du wirst freudig begrüßt und man weist dir einen Platz zu. Die Gewissheit, dass nur Menschen Zugang zu dieser Halle haben, erlaubt dir dich einmal zu entspannen und mit deinen Freunden zu feiern.', 0, 0, 0, '`&Weil du ein Mensch bist, bekommst du `^1`& Waldkampf zusätzlich!', 'a:1:{s:5:"turns";s:1:"1";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('hvr', 'Hoverschaf', '`rHoverschaf', 'Hoverschafe', '`rHoverschafe', 0, '', 'Irre oder was?! Hoverschafe gibt''s ja gar nicht.', 'Geheim, geheim.', 0, '', '', '', 1, 1, 0, 'Als Hoverschaf hoverst du durch die Lüfe und bekommst jeden Bonus nachgeschmissen.', 'a:5:{s:6:"attack";s:2:"10";s:7:"defence";s:2:"10";s:12:"maxhitpoints";s:2:"10";s:5:"turns";s:2:"10";s:11:"castleturns";s:2:"10";}', 'a:8:{s:8:"thievery";s:2:"10";s:5:"druid";s:2:"10";s:7:"darkart";s:2:"10";s:8:"jugglery";s:2:"10";s:9:"cattiness";s:2:"10";s:7:"heroism";s:2:"10";s:5:"magic";s:2:"10";s:13:"transmutation";s:2:"10";}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('elf', 'Elf', '`^Elf', 'Elfen', '`^Elfen', 1, '', '`^Als Elf bist du dir immer allem bewusst, was um dich herum passiert. Nur sehr wenig kann dich überraschen.`nDu bekommst einen zusätzlichen Punkt auf deinen Verteidigungswert!', 'Du wurdest hoch über den Bäumen des Waldes Glorfindal geboren, in zerbrechlich wirkenden, kunstvoll verzierten Bauten der `^Elfen`0, die so aussehen, als ob sie beim leisesten Windhauch zusammenstürzen würden und doch schon Jahrhunderte überdauern.', 1, 'Elfenhain', 'Zum Elfenhain', '`tDu folgst deinen feinen Sinnen, in der Gewissheit, dass sie dich zu dem geheimen, verborgenen Ort führen mögen. Und dann erblickst du auch schon die kleinen Baumhäuser, die so elegant anmuten, als seien sie natürlich in den Baum gewachsen. Ein wohliges Kribbeln durchfährt deinen Körper, als du die ersten Elfen erblickst, die dir freudig aus den Höhen der Bäume entgegen winken. Du bist froh hier zu sein, keine stinkenden Orks, keine lauten Trolle. Du geniesst die Stille und die Gesellschaft deiner Freunde, in der ruhigen Gewissheit, dass es nur Elfen möglich ist diesen Ort zu finden.', 0, 0, 0, '', 'a:1:{s:7:"defence";s:1:"1";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('trl', 'Troll', '`2Troll', 'Trolle', '`2Trolle', 1, '', '`2Als Troll warst du immer auf dich alleine gestellt. Die Möglichkeiten des Kampfs sind dir nicht fremd.`n`^Du erhältst einen zusätzlichen Punkt auf deinen Angriffswert!', 'Geboren wurdest du in den Sümpfen von Glukmoore als `2Troll`0, auf dich alleine gestellt seit dem Moment, als du aus der lederartigen Hülle deines Eis geschlüpft bist und aus den Knochen deiner ungeschlüpften Geschwister ein erstes Festmahl gemacht hast.', 1, 'Trollfeste', 'Zur Trollfeste', '`tDu betrittst die mächtige Trollfeste. Endlich, aber auch endlich kannst du dich einmal frei bewegen, ohne die Angst bei jedem Schritt wogegen zu stoßen oder dir beim Durchschreiten einer Türschwelle den Kopf anzuschlagen.`nHier in der Trollfeste bist du unter Deinesgleichen. Nur Trolle wohin du blickst, kein menschliches Gewürm oder sonstiges störendes Getier.`nHier fühlst du dich wohl und hier sind auch die meisten deiner Freunde zu finden. Das Ale fließt in Strömen und es gibt so viel Elf am Spieß wie du magst. Die Stimmung ist herrlich.', 0, 0, 0, '', 'a:1:{s:6:"attack";s:1:"1";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('zwg', 'Zwerg', '`#Zwerg', 'Zwerge', '`#Zwerge', 1, '', '`#Als Zwerg fällt es dir leicht, den Wert bestimmter Güter besser einzuschätzen.`n`^Du bekommst mehr Gold durch Waldkämpfe!', 'Aufgewachsen bist du tief in der unterirdischen Festung Qexelcrag, der Heimat der edlen und starken `#Zwerge`0, deren Verlangen nach Besitz und Reichtum in keinem Verhältnis zu ihrer Körpergrösse steht.', 2, 'Zwergenbinge', 'Zur Zwergenbinge', '`tDu eilst durch das Wohnviertel, vorbei an den Wohnhäusern und Lagerhallen, bis du in immer dünner besiedeltes Gebiet kommst. Allmählich wird es immer hügeliger. Du steuerst zielsicher auf einen großen Strauch zu und schlägst ihn auf Seite. Dahinter führt eine steinerne Treppe mindestens 1000 Stufen tief ins Erdreich hinab. Du folgst der Treppe und sie endet vor einer kleinen stählernen Türe. Du rufst dein Losungswort und die Tür schwingt auf. Dahinter bietet sich dir ein wahrlich freudiger Anblick : Gemütliche, kleine Räumchen, nicht so groß und weit dass man sich drin verlieren könnte, wie sie es in der Menschenwelt sind. Wohin dein Auge auch nur blickt siehst du Zwerge, beim Saufen, Gröhlen und beim Tratsch. Hier bist du richtig und hier kannst du es dir für eine Weile richtig gut gehen lassen. Du weißt dass niemand außer Zwergen hierhin Zugang hat und setzt dich auf ein großes Bierfass, was du zu leeren beabsichtigst.', 0, 0, 0, '', 'a:0:{}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('ecs', 'Echsenwesen', '`5Echsenwesen', 'Echsenwesen', '`5Echsenwesen', 1, '', '`5Als Echsenwesen hast du durch deine Häutungen einen klaren gesundheitlichen Vorteil gegenüber anderen Rassen.`n`^Du startest mit einem permanenten Lebenspunkt mehr!', 'In einem Erdloch in der öden Landschaft weit außerhalb jeder Siedlung bist du als `5Echsenwesen`0 aus deinem Ei geschlüpft. Artverwandt mit den Drachen hast du es nicht leicht in dieser Welt.', 1, 'Echsensümpfe', 'In die Echsensümpfe', '`tDu begibst dich in die tiefen Sümpfe. Der Boden unter deinen Füßen wird immer feuchter und gibt immer mehr nach. Dank den feinen Häuten zwischen deinen Zehen gehst du jedoch nicht unter, sondern kannst dich sicher immer weiter und tiefer in die Sümpfe begeben. Jeder Andere, so bist du dir sicher, wäre schon längst versunken und des Todes. Dann, nach einer Weile des sumpfigen Marsches erblickst du die erste Behausung, die aus dem Sumpfboden ragt. Hier bist du aufgewachsen und hierhin zieht es dich auch immer wieder zurück. Du weißt, dass nur Echsen diesen Ort betreten können und schaust dich nach deinen Freunden und Bekannten um. Dann beschließt du für eine Weile hier zu bleiben und es dir hier gut gehen zu lassen. Der süßlich faulige Geruch der Sümpfe, den du im Dorf so sehr vermisst hast, dringt in deine Nase und du hast das schöne Gefühl zuhause zu sein.', 0, 0, 0, '', 'a:1:{s:12:"maxhitpoints";s:1:"1";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('dkl', 'Dunkelelf', '`5Dunkelelf', 'Dunkelelfen', '`5Dunkelelfen', 1, '', '`5Dunkelelfen kennen Zeit ihres Lebens nichts anderes als Schmerz und Leid. Durch deine Agressivität startest du mit einem Angriffspunkt mehr!', 'In schattigen Baumhöhlen des Waldes Glorfindal als `5Dunkelelf`0 geboren, wurdest Du durch Deine elfischen Brüder gedemütigt, weil Du anders warst als diese. Grimmig wandest Du Dich ab und suchtest Dein Heil in dunklen Höhlen und hast fortan Deine eigenen finsteren Pläne geschmiedet.', 1, 'Finsterwald', 'Zum Finsterwald', '`tDu verlässt die festen Wege und gehst geradewegs in den Wald hinein. Anfangs ist der noch dünn bewachsen und recht hell. Schnellen Schrittes huschst du durch durch diesen Ort der Lichtkriecher und allmählich gelangst du immer tiefer in den Wald. Die Bäumen scheinen hier größer und älter, ihr Blätterdach dichter. Du gehst weiter. Es wird immer dunkler und deine Augen leuchten violett auf. Du kannst natürlich auch bei dieses Dunkelheit hervorragend sehen und weißt auch genau wo du hin musst. Dann erreichst du dein Ziel, den Ort, an dem du deine Kindheit verbracht hast. Und du bist hier nicht allein, viele andere Dunkelelfen haben sich auch hier eingefunden. Du weißt dass es nur Dunkelelfen möglich ist diesen Ort zu betreten und dass du die nächste Zeit Ruhe vor den unwürdigen Geschöpfen des Dorfes haben wirst. Leise Stimmen und hämisches Kichern dringen an dein Ohr vor. Du beschließt eine Weile hier zu verweilen und mit Deinesgleichen zu verbringen.', 0, 0, 0, '', 'a:1:{s:6:"attack";s:1:"1";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('wwf', 'Werwolf', '`TWerwolf', 'Werwölfe', '`TWerwölfe', 1, '', '`TWerwölfe`0 sind augenscheinlich gewöhnliche Menschen, die sich aber bei Mondlicht in gefährliche Kreaturen verwandeln. Sie sind unwahrscheinlich flink und aggressiv im Angriff.`n`^Du erhältst 3 Angriffspunkte, verlierst aber 2 Verteidigungspunkte.', 'In Romar wurdest du als Mensch einmal von einem Wolf angefallen. Nun vollzieht sich mit dir bei Vollmond eine schaurige Wandlung und du wirst zum furchterregenden `TWerwolf`0.', 1, 'Werwolflichtung', 'Zur Werwolflichtung', '`tDu folgst den Waldwegen ein kleines Stück und lässt dich von deinen Instinkten leiten.`nDer Wald wird immer dichter und die Sicht immer schlechter, doch irgendwie scheinst es dir nichts auszumachen. Du hast deine Witterung aufgenommen und eilst zielstrebig durch das dichte Unterholz.`nDann hast du sie erreicht : Eine kleine Lichtung mitten im tiefen Wald.`nNeugierig blickst du dich um und entdeckst weitere Wesen, die dir ähnlich scheinen.`nDu weißt, dass sie wohl das selbe Schicksal wie du erleiden müssen und obwohl einige von ihnen furchterregend aussehen hast du keine Angst.`nAuch weißt du, dass dieser Ort ein ganz besonderer Ort ist, zu dem nur Deinesgleichen Zugang hat.`nAlso setzt du dich für ein Weilchen auf einen Stein und suchst das Gespräch mit den Anderen.', 0, 0, 5, '', 'a:2:{s:6:"attack";s:1:"3";s:7:"defence";s:2:"-2";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('gbl', 'Goblin', '`6Goblin', 'Goblins', '`6Goblins', 1, '', '`6Als Goblin ist die Welt ein gefährlicher Ort für dich. `n Du bist stark und kannst dich verteidigen. `n `^Du erhältst einen Angriffs und Verteidigungspunkt, aber du hast weniger Waldkämpfe!', 'Als `6Goblin`0 hast du kleinere Ratten und ähnliches gejagt, bis dich ein Dunkelelf entdeckte und mit in die Welt der Menschen brachte. Jetzt konntest du endlich aus der Sklaverei fliehen und beginnst ein neues Leben.', 2, 'Goblinbau', 'In den Goblinbau', '`tDu flitzt durch das Wohnviertel, huschst an Häuserecken vorbei und verlässt das Wohnviertel, eilst zum hügeligen Gebiet abseits. Eine ganze Weile bist du unterwegs, bis du die gut versteckten Hügelhäuser entdeckst, die deine Sippe errichtet hat. Du schlüpfst durch einen der schmalen kleinen Eingänge und weißt, dass nicht einmal ein Menschenkind diese schmalen Pforte passieren kann und dass sich hier nur Goblins aufhalten. Du begibst dich tief in das halbunterirdische Gewölbe und triffst viel andere Goblins. Ein wohl bekanntes Geschreie tritt an dein Ohr und du fühlst dich daheim. Hier bist du mit deinen Freunden allein und niemand wird eure Zusammenkunft stören können. Du beschließt eine Weile hier zu bleiben bevor du dich wieder in die gefährliche Welt der Menschen begibst.', 0, 0, 0, '`&Weil Du ein Goblin bist, erhältst du `^1`& Waldkampf weniger!', 'a:3:{s:6:"attack";s:1:"1";s:7:"defence";s:1:"1";s:5:"turns";s:2:"-1";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('ork', 'Ork', '`2Ork', 'Orks', '`2Orks', 1, '', '`4Als Ork `0 führst du ein einsames, nomadisches Lebens und bist ein ausgezeichneter Kämpfer. `^ Du erhältst 2 Angriffspunkte und einen Verteidigungspunkt, aber weniger Waldkämpfe!', 'Im Nördlichen Wald, unter Bäumen und in Dunkelheit leben die `#Orks`0. Sie sind Wanderer der Wälder und Berge. Jetzt haben dich deine Wanderschaften an diesen Ort gebracht.', 1, 'Orkfeste', 'In die Orkfeste', '`tDu holst die alte Karte hervor und folgst den verschlungenen Waldwegen, dein Ziel stets vor Augen. Obwohl es schwierig ist diesen Ort zu finden, erreichst du mit Hilfe der Karte endlich die Orkfeste. Du stellst dich vor das mächtige Eingangsportal und stößt einen markerschütternden Schrei aus. Schon öffnet sich die Türe und du wirst herein gelassen. Die Atmosphäre im Inneren der Orkfeste gefällt dir sehr. Viele andere Orks haben sich hier eingefunden und sind mit Speis und Trank, dem Erzählen wilder Kriegsgeschichten und Raufereien beschäftigt. Kaum hast du dich auf einer Holzbank niedergelassen drückt dir ein anderer Ork schon einen riesigen Krug Ale und eine Menschenkeule in die Hand. Hier kannst du es dir nun so richtig gut gehen lassen bevor du wieder zurück ins Dorf gehst.', 0, 0, 0, '`&Weil Du ein Ork bist, erhältst du`^2`& Waldkämpfe weniger!', 'a:3:{s:6:"attack";s:1:"2";s:7:"defence";s:1:"1";s:5:"turns";s:2:"-2";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('vmp', 'Vampir', '`4Vampir', 'Vampire', '`4Vampire', 1, '', '`4Als Vampir `0 wandelst du unter den Lebenden und bist ein Vertreter der dunklen Künste. Du erhältst Punkte in den dunklen Künsten, verlierst aber einen Verteidigungspunkt.', 'Hoch thronend in einem Schloss hast du als `4Vampir`0 die Jahrhunderte überlebt. Jetzt treibt dich dein Blutdurst wieder zurück in die Welt. Als "Blutlutscher", wie dich manche Menschen auch abfällig nennen, befindest du dich stets auf der Hut, deine wahre Identität zu verbergen.', 2, 'Mausoleum', 'Zum Mausoleum', '`tDu schleichst durch Wohnviertel und näherst dich zielsicher dem Friedhof. Vorbei an unzähligen Gräbern und Gruften bahnst du dir deinen Weg bis hin zu einem ganz bestimmten Bau. Du legst deine Hände auf das steinerne Türportal und es bewegt sich langsam auf Seite um kurz nachdem du das Mausoleum betreten hast, wieder hinter dir zuzuschlagen. Du gehst einen langen Gang entlang und steigst eine Treppe hinab. Deine Schritte hallen dumpf nacht. Dann gelangst du in einen großen Raum, in dem sich schon andere Vampire eingefunden haben. Viele von ihnen kennst du bereits. Sie hängen an der Decke, ruhen oder unterhalten sich. Ein paar der Vampire haben eine junge vor Schrecken starre Menschenfrau mitgebracht, die sie der Reihe nach durchreichen um von ihr zu trinken. Du weißt dass sich hier nur Vampire aufhalten, und das verleiht dir ein Gefühl von Sicherheit. Du setzt dich auf einen Sarg und winks den anderen Vampiren, die du kennst, zu.', 0, 0, 0, '`&Als Vampir erhältst du 2 Anwendungen in dunklen Künsten zusätzlich!', 'a:1:{s:7:"defence";s:2:"-1";}', 'a:1:{s:7:"darkart";s:1:"2";}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('hbl', 'Halbling', '`tHalbling', 'Halblinge', '`tHalblinge', 1, '', 'Als `4Halbling `0bist du klein und unscheinbar. Du bist zwar schwach, aber geschickt und weisst genau wie du die Leute um ihre Taschen erleichtern kannst. Du erhältst 3 Skillpunkte der Diebeskunst, verlierst aber jeweils einen Punkt in Angriff und Verteidigung.', 'Aufgewachsen in den kleinen Hügeldörfern von Cloudshire, als `tHalbling`0, hattest du schon immer lange und geschickte Finger und nichts war vor dir sicher.', 2, 'Hügelhäuser', 'Zu den Hügelhäusern', '`tDu schlendest durch das Wohnviertel und suchst die Weiten der grünen Ebenen weit abseits der dicht besiedelten Strassen. Dort ist es hügelig und die Natur ist unberührt.`nSchliesslich gelangst du an einen großen hohlen Baum. Dort gibst du das geheime Klopfzeichen, dass dich deine Eltern einst gelehrt haben und wenig später fällt dir eine winzig kleine Tür in einem der Hügel auf. Gerade groß genug dass du hindurchschlüpfen kannst.`nDu betrittst das kleine Häuschen und entdeckst, dass sich viele andere Halblinge hier versammelt haben, die dich mit einem guten Becher Ale und einer frisch gestopften Pfeife begrüßen.`nDu bist dir ganz sicher, dass ihr hier unter euch seid und entschliesst ein wenig mit deinen Artgenossen zu feiern.', 0, 0, 0, '`&Als Halbling erhältst du 3 Anwendungen in Diebeskünsten zusätzlich!', 'a:2:{s:6:"attack";s:2:"-1";s:7:"defence";s:2:"-1";}', 'a:1:{s:8:"thievery";s:1:"3";}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('dmn', 'Dämon', '`4Dämon', 'Dämonen', '`4Dämonen', 1, '', 'Als niederer `4Dämon `0führst du ein Doppelleben in der Gesellschaft der Menschen, immer darum bemüht, dass du nie ungewollt als das erkannt wirst, was du bist. Deine ungeheure Stärke und Zähigkeit machen dich zu einem hervorragenden Kämpfer und Jäger. Du erhältst jeweils 2 Punkte in Angriff und Verteidigung, jedoch hast du drei Waldkämpfe weniger zur Verfügung!', 'Aufgewachsen bist du unter den Toten bei Ramius, in der Welt der `$Dämonen`0. Du hast immer nur zu deinem Vater aufgesehen und bist jedem seiner Schritte gefolgt, bis er eines Tages verschwand und du auf eigenen Beinen stehen musstest. Schwarze Schwingen kennzeichnen deine Herkunft.', 1, 'Schwefelquellen', 'Zu den Schwefelquellen', '`tDu schreitest durch den tiefen Wald, knickst dünne Bäume und Sträucher um und schlägst eine Schneise durch den dichten Wuchs. Du weißt genau wo du hin willst und plötzlich vernimmst du auch diesen wohlig stechenden Geruch in deiner Nase. Nur ein paar Schritte später liegen sie vor dir : die blubbernden heißen Quellen. Der beißende Schwefelegeruch ist so stark, dass kein Mensch und auch kein anderes diesseitiges Wesen hier überleben kann.`nDu kannst dir ganz sicher sein, dass du hier nicht gestört wirst und erblickst auch schon ein paar andere Dämonen, die sich ebenfalls hier hin zurück gezogen haben.', 0, 0, 30, '`&Weil Du ein Dämon bist hast du `^3`& Waldkämpfe weniger!', 'a:3:{s:6:"attack";s:1:"2";s:7:"defence";s:1:"2";s:5:"turns";s:2:"-3";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('slm', 'Schelm', '`9Schelm', 'Schelme', '`9Schelme', 1, '', '`4Als Schelm`0 ist dir so ziemlich alles egal, was deine stets gute Laune trüben könnte. Denn bei den Feen hast du gelernt was es bedeutet Spass zu haben. Und so bist du für jede Albernheit zu haben. Jedoch solltest du aufpassen wem du einen Streich spielst, denn du bist recht schwach im Kampf. Durch deinen Frohmut und dein hohes Geschick erhältst du 3 zusätzliche Anwendungen in Gaukelei! Deine Stimmung wird nie schlecht sein!`nAber du verlierst jeweils 2 Punkte in Angriff und Verteidigung!', 'Geboren wurdest du in einem kleinen Dorf etwas südlich von hier, doch konntest du dich nicht lange an deiner Heimat erfreuen, da dich die Feen schon in der ersten Nacht nach deiner Geburt aus dem Bett stahlen und dich in ihre Welt entführten. Dort hast du gute 15 Jahre unter den Feen verbracht, bis diese eines Tages feststellten, dass du mit zunehmendem Alter immer mehr zur Spassbremse wirst. Also haben sie dich kurzerhand aus ihrem Reich verbannt. Seitdem streift du umher, als `9Schelm`0, durchtrieben von einem ganz besonderen Humor und immer auf der Flucht vor den Opfern deiner letzten Scherze.', 2, 'Schelmenraum', 'In den Schelmenraum', '`tDu tänzelst durch das Wohnviertel auf der Suche nach einem ganz bestimmten Haus.`nDann hast du es gefunden : groß und seriös wirkend nimmt es seinen Platz unter den anderen Häusern ein. Du kletterst durch ein Kellerfenster hinein, steigst eine Treppe hinauf und öffnest eine schwere Eichentüre.`nUnd dann siehst du sie : Andere, die so sind wie du, füllen einen großen Raum. Lärm und Gelächter schlägt dir entgegen und du fühlst dich ein wenig an deine Kindheit bei den Feen erinnert. Du lauschst den Gesprächen, die sich für dich schnell als totaler Unsinn oder Berichte über Scherze mit argwöhnischen Menschen und Elfen herausstellen.`nHier fühlst du dich wohl, denn du weißt, dass niemand, der kein Schelm auch nur für eine Minute in diesem Raum verweilen könnte.', 0, 0, 15, '`&Als Schelm erhältst du 3 Anwendungen in Gaukelei zusätzlich!', 'a:2:{s:6:"attack";s:2:"-2";s:7:"defence";s:2:"-2";}', 'a:1:{s:8:"jugglery";s:1:"3";}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('eng', 'Engel', '`^Engel', 'Engel', '`^Engel', 1, '', 'Als `4Engel`0 warst du einst ein reines Wesen, geboren aus Licht und mit der Macht die Heerscharen der Himmel zu befehligen.`nNun aber bist du in die Welt der Sterblichen vorgedrungen, und obwohl deine Kräfte noch immer vorhanden sind, sind sie hier lediglich eine Spur, verglichen mit dem was sie einst waren, deine Flügel nur sichtbar für Wesen von reinem Herzen.`nWas dich hierher trieb weißt nur du selbst, doch du spürst, dass die Boshaftigkeit hier deines reines Wesen verdirbt und dich in die Schatten zu ziehen droht.`nDu erhältst 2 Verteidigungspunkte, verlierst aber auch einen Angriffspunkt.', 'Geboren aus reinem Licht bist du ein `^Engel`0. Du hast diese unwürdige Welt aus einem ganz bestimmten Grund betreten, doch diesen Grund kennst nur du allein.', 1, 'Wolkenfestung', 'In die Wolkenfestung', '`tDu schreitest durch den Wald, frei und unbekümmert. An einer Lichtung holst du dann tief Luft und löst dich von der beklemmenden Hülle, die dir dein Körper ist.`nDu schwebst hinauf wie du es gewohnt bist, hoch zu den Wolken. Die Welt unter dir wird immer kleiner und unwirklicher. Als du das Wolkendach durchbrochen hast stellst du fest, dass du nicht allein bist.`nAndere haben sich wie du hierher zurückgezogen, um sich von den Strapazen der Sterblichkeit zu erholen. Du weißt dass euch hier niemand Anderes stören kann und verweilst ein wenig bei Deinesgleichen.', 0, 0, 50, '', 'a:2:{s:6:"attack";s:2:"-1";s:7:"defence";s:1:"2";}', 'a:0:{}');
INSERT INTO `races` (`id`, `name`, `colname`, `name_plur`, `colname_plur`, `active`, `loc_desc`, `chosen_msg`, `long_desc`, `raceroom`, `raceroom_name`, `raceroom_nav`, `raceroom_desc`, `raceroom_all`, `superuser`, `mindk`, `newday_msg`, `boni`, `specboni`) VALUES ('avt', 'Avatar', '`&Avatar', 'Avatare', '`&Avatare', 1, '', 'Als `4Avatar`0 bist du ein Wesen, dass außerhalb von Gut und Böse, von Licht und Schatten steht. Es gibt nur Einen, der noch über dir steht, und das ist dein Gott, dem du die ewige Treue geschworen hast. Neben dir gibt es noch Andere deinesgleichen, doch du hast das Gefühl, dass du ihm der Liebste von Allen bist.`nDeshalb hat er dich wohl auch in die Welt der Sterblichen geschickt, wo du als seine Inkarnation in seinem Namen unterwegs bist.`nDu bist zwar nun an die Gesetze dieser Welt gebunden, doch stehst du über Leben und Tod.`n`nDu erhältst zusätzlich 3 Anwendungen in Mystischen Kräften und kannst jeden Rassenraum betreten, verlierst jedoch auch einen Angriffspunkt.', 'Entstanden in himmlichen Sphären, als `&Avatar`0, bist du ein Wesen ohne Form und ohne Anfang oder Ende. Du bist nur einem verpflichtet: deinem Gott. Nun hat er dich in diese Welt gesandt, gepresst in die Hülle eines kurzlebigen Sterblichen.', 1, 'Leere', 'In die Leere', '`tDu blickst dich kurz um, sicherstellend, dass die niemand beobachtet, und streckst deine Hände aus um das dünne Gewebe der Wirklichkeit zu zerreissen.`nDahinter ist nichts, die Leere, und du trittst ein in ein schwarzes Wabern.`nDu verlässt die Grenzen von Zeit und Raum und kehrst zum Ursprung zurück. Langsam spürst du wie deine Kräfte vollends zu dir zurück kehren. Du spürst die Präsenz anderer Avatare.`nHier seid ihr nun unter Euch, da dieser Ort Ausserhalb jeglicher Vorstellungskraft anderer Wesen existiert.', 1, 0, 100, '`&Als Avatar erhältst du 3 Anwendungen in Mystischen Kräften zusätzlich!', 'a:1:{s:6:"attack";s:2:"-1";}', 'a:1:{s:5:"magic";s:1:"3";}');


# random_commentary:

INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (35, 'marketplace', '/msg Eine Bäuerin verlangt lautstark nach etwas Ale, um ihre trockene Kehle zu benetzen', 1, 1, 2, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (36, 'marketplace', '/msg `3Ein fahrender Händler läuft mit einem Bauchladen voller verschiedenster Dinge über den Marktplatz, während er seine Waren anpreist. `#"Saftige Äpfel! Ofenwarme Brezeln! Frische Elfenohren!"', 2, 1, 0, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (37, 'all_outside', '/msg `gGroße Schneeflocken segeln langsam vom Himmel herab und bedecken alles mit einer feinen, weißen Schicht. ', 20, 1, 10, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (38, 'village', '/msg `)Ein Tentakel kommt aus dem Brunnen und tastet einen Moment ziellos auf dem Brunnenrand umher ehe es sich wieder in die finstere Tiefe verzieht.', 2, 1, 0, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (39, 'village', '/msg `!Eine Elfe hüpft über den Platz, eine fröhliche Melodie vor sich hinsummend und öffnet schwungvoll die Schenkentür, um darin zu verschwinden. ', 2, 1, 0, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (40, 'village', '/msg `TEin struppiger Hund rennt laut kläffend über den Platz, eine kleine Katze vor sich herjagend.', 2, 1, 0, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (41, 'marketplace', '/msg `&Wie auf ein Kommando hin erhebt sich ein Schwarm Tauben über dem Platz und fliegt davon.', 5, 1, 0, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (42, 'garden', '/msg `gEin sanfter Windstoß treibt ein paar hauchzarte Blütenblätter vor sich her, bis sie sich auf die Oberfläche des Flusses legen und von den Wellen fortgetragen werden.', 50, 1, 0, 4, 6, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (43, 'all_outside', '/msg `tAm Himmel ist die Silhouette eines großen Raubvogels zu sehen, der sich von den warmen Lüften tragen lässt und Ausschau nach seiner nächsten Beute hält.', 20, 1, 5, 1, 12, 0, 23, '0000-00-00');
INSERT INTO `random_commentary` (`id`, `section`, `comment`, `chance`, `gap`, `weather`, `month_min`, `month_max`, `hour_min`, `hour_max`, `rldate`) VALUES (44, 'pool', '/msg `9Ein Fisch springt mit einem lauten Platschen aus dem Wasser und hinterlässt ein paar kleine Wellen, die sich auf der Wasseroberfläche ausbreiten bis sie schließlich verschwinden.', 2, 1, 0, 1, 12, 0, 23, '0000-00-00');


