# suspicious_mail:

CREATE TABLE `suspicious_mail` (
  `messageid` int(11) unsigned NOT NULL auto_increment,
  `msgfrom` int(11) unsigned NOT NULL default '0',
  `msgto` int(11) unsigned NOT NULL default '0',
  `subject` varchar(50) collate latin1_german1_ci NOT NULL default '',
  `body` text collate latin1_german1_ci NOT NULL,
  `sent` datetime NOT NULL default '0000-00-00 00:00:00',
  `seen` tinyint(1) NOT NULL default '0',
  `ip` varchar(15) collate latin1_german1_ci NOT NULL default '0.0.0.0',
  PRIMARY KEY  (`message

id`),
  KEY `msgto` (`msgto`),
  KEY `seen` (`seen`),
  KEY `ip` (`ip`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_german1_ci AUTO_INCREMENT=1 ;


# suspicious_words

CREATE TABLE `suspicious_words` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(100) character set latin1 NOT NULL default '',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_german1_ci AUTO_INCREMENT=8 ;

