# waldspecial:

REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (2, 'aphrodite.php', 'Quickie mit einer Göttin.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (3, 'audrey.php', 'Eine verrückte Frau, bei der man einen WK verlieren oder bis zu 5 bekommen kann.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (4, 'bushes.php', 'Entweder man verliert all sein Gold und bekommt DP dafür, oder man findet welches (oder auch mal gar nichts).', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (5, 'castle.php', 'Möglichkeit, die Orkburg zu betreten. Spieler ohne DK müssen sich den Weg freikämpfen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (6, 'cookies.php', 'Man findet einen Keks im Wald. Das hat manchmal positive, meist negative Auswirkungen', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (7, 'darkhorse.php', 'Man findet einen Weg in die Darkhorse Taverne.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (8, 'distress.php', 'Man bekommt den Auftrag, jemanden zu retten und hat 3 Orte zur Auswahl. Von Tod über Charme bis Reichtümer ist alles möglich.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (9, 'fairy1.php', 'Fee verlangt einen Edelstein, gibt auch meist Belohnung. Wenn man sich weigert besteht die Möglichkeit zu einem ~entzückenden~ Flauschihasen zu werden.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (10, 'findgem.php', 'Man findet einen Edelstein.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (11, 'findgold.php', 'Man findet etwas Gold.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (12, 'findtreasure.php', 'Ein hohler Baumstamm in dem man eine Schatzkarte, einen Edelstein oder wütende Bewohner finden kann.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (13, 'gladiator.php', 'Ein Gladiator, der den Spieler aber einer gewissen Anzahl Arenapunkte belohnt.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (14, 'glowingstream.php', 'Ein kleiner Bach, man kann davon trinken und man kann es sein lassen. Belohnung oder Tod.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (15, 'goldenegg.php', 'Wenn das Goldene Ei keinen Besitzer hat, kann man es mitnehmen, ansonsten erfährt man, wer es hat.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (16, 'goldmine.php', 'Eine Miene, in der man nach Gold und Edelsteinen graben kann. VORSICHT: Bei Einsturz kann man die Hälfte seiner Edelsteine verlieren und das Tier noch dazu. Besser ist Finger weg :D', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (17, 'grassyfield.php', 'Eine Lichtung, auf der man die LP regeneriert und das Tier seine Runden zurückbekommt. Da auch Zugang über \\"spuken\\" durch die Unterwelt sehr gerne zugespammt.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (18, 'lake.php', 'Ein See, bei dem man einen Überblick über seinen Charme und sein Ansehen bekommen kann, oder vom Wasser trinken - auf eigene Gefahr.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (19, 'necromancer.php', 'Ein unheimlicher alter Mann mit Verbindung zur Unterwelt.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (20, 'oldmanbet.php', 'Man muss in 6 Zügen eine Zahl zwischen 1 und 100 erraten.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (21, 'oldmanpretty.php', 'Bekomme einen Charmepunkt.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (22, 'oldmantown.php', 'Bringe einen alten Mann zurück ins Dorf und erhalte eine Belohnung (Charme oder ES).', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (23, 'oldmanugly.php', 'Verliere einen Charmpunkt.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (24, 'riddles.php', 'Man bekommt ein Rätsel gestellt, bei richtiger Lösung gibt es eine Belohnung.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (25, 'sacrificealtar.php', 'Ein Altar auf dem man sich selber, Blumen oder einen Edelstein opfern kann. Nur wie die Götter auf das Opfer reagieren, das wissen nur.. die Götter eben.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (26, 'skillmaster.php', 'Erhöhe deinen Skillevel für einen Edelstein.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (27, 'slump.php', 'Man verliert Gold das vom letzten Drachentöter und dem neuesten Spieler aufgesammelt wird.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (28, 'smith.php', 'Die Möglichkeit für einen Edelstein eventuell die Waffe verbessern zu lassen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (29, 'stonehenge.php', 'Man kann den Steinkreis betreten oder es lassen.. vom Tod bis hin zum Edelstein ist dort alles zu finden.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (30, 'stumble.php', 'Man verliert ein paar LP, kann sterben, wenn man zu wenige hat.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (31, 'tempel.php', 'Dies ist der Tempel wo man eine Summe Spenden kann \\"100..500..1000..5000\\"\r\nund wen man der jenige ist der den Pott vollgemacht hat..10000 Goldstücke ..so erscheint eine Gottheit mit einer Belohnung.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (32, 'vampire.php', 'Der gute alte Vampir. Nimmt bei Spielern, die über LP-Grenze sind, LP, ansonsten, bei Spielern, die mehr als 250 Charme besitzen, den Charme. Kann durch LP-Geschenk besänftigt werden.\r\nAnsonsten gar nichts.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (33, 'wannabe.php', 'Man wird vom Ritter Möchtegern angegriffen, den man verfolgen kann - oder es lassen.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (34, 'waterfall.php', 'Der Pfad führt zu einem Wasserfall wo man so ziemlich alles bekommen kann. Und sterben.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (35, 'randdragon.php', 'Der Grüne Drache, eine reizende Begegnung, solange man sich schön unterwürfig zeigt.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (36, 'bridge.php', 'Eine Brücke im Wald mit sehr geringen Überlebenschancen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (37, 'pig.php', 'Ein Wildschwein, das man verfolgen kann.. kann Gold oder ES bringen und man verliert ein paar LP.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (38, 'mud.php', 'Man könnte es auch als sinnlos bezeichnen ;)', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (39, 'riverbath.php', 'Kleine Chance auf einen Edelstein.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (40, 'remains.php', 'Man findet die Leiche eines anderen Kriegers und kann sie begraben oder durchsuchen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (41, 'graeultat.php', 'Leichen im Wald, ist man dumm genug, sie sich anzusehen, macht man vermutlich bekanntschaft mit einer sehr unfreundlichen Hexe.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (42, 'edelsteinbrunnen.php', 'Chance, bis zu 5 Edelsteine zu verdoppeln oder zu verlieren.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (43, 'searchdwarfs.php', 'Ein Traum bringt den Spieler auf die Idee, ein paaar Zwerge ausrauben zu gehen (als Zwerg, einen Verwandten besuchen zu gehen).', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (44, 'forestlake.php', 'Der Trampelpfad für Verliebte.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (45, 'jewelrymaker.php', 'Für alles Gold ein kleines Stück Elfenkunst bekommen, das oftmals ein paar Edelsteine wert ist.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (46, 'terronville.php', 'Ein seltsamer Kerl, der dem Spieler einen Nestaffen verkauft, ihm Gold stiehlt oder ihm Erfahrung bringt.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (47, 'statue.php', 'Eine Statue, die gerne mit Angr und Vert Werten spielt ;)', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (48, 'weather.php', 'Abhängig vom Wetter werden dem Spieler ein paar Attribute geändert, er bekommt Schnupfen, Mücken nerven ihn etc.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (49, 'healer_special.php', 'Ein Heiler rückt entweder einen Trank, der schadet oder heilt raus oder einen Gutschein für Golindas Hütte.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (50, 'race.php', 'Man wird zum Rennen/Wettkampf mit dem jeweiligen Tier herausgefordert.. bei Niederlage kann man die Hälfte der ES verlieren - FINGER WEG *g*', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (51, 'stiefel.php', 'Ein grauenhaft stinkender Stiefel in dem manchmal etwas steckt. Dummerweise kann man auch erschossen werden, wenn man hineinsieht.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (52, 'time.php', 'Je nach Spielzeit ein anderes kleines Ereignis.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (53, 'cliff.php', 'Unebenes Gelände, man kann LP verlieren oder kurzzeitig hinzubekommen, das Tier kann alle Runden verlieren oder man findet einen Edelstein.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (54, 'schimaere.php', 'Eine Schimaere.. greife sie an und finde reus, ob sie echt ist. Kann perm LP geben/nehmen oder andere nette Dinge.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (55, 'bellerophontes.php', 'Man findet den Turm von Bellerophotes im Wald und hat die Chance Gold&ES, perm. LP oder Exp zu bekommen oder zu sterben.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (56, 'liana.php', 'Lianen, die einfach so im Wald rumhängen.. können Exp oder WK geben, aber auch LP verlieren lassen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (57, 'trapper.php', 'Ein Trapper.. man kann LP verlieren, Gold bekommen oder Pilze mit verschiedener Wirkung erwerben.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (58, 'moocher.php', 'Man wird von Räubern überfallen und hat die Wahl, das Gold herzugeben oder gegen die Übermacht zu kämpfen. Hat man kein Gold dabei und gibt es her = -1 perm. LP!', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (59, 'derfremde.php', 'Man begegnet Ramius im Wald. Kann Gefallen bringen, den \\"Ramius\\'' Sklaven\\" Titel, einen töten oder Gold geben.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (60, 'cruxis.php', 'Special für High Level Charaktere, man bekämpft Engel, die sehr schwer zu besiegen sind, die Belohnung ist gering', 2, 30, 0, 0);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (62, 'surprise.php', 'Überraschungsgeschenk an bestimmten Spieler.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (63, 'bumpiness.php', 'Man tritt in ein Loch und verliert viele LP/das Tier verliert viele Runden.', 3, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (64, 'eaoden.php', 'Möglichkeit, für 10 Edelsteine das starke Kettenhemd eines Kriegers zu bekommen, oder das schlechte dünne Kettenhemd. Kann bei wiederholtem Besuch noch stärker werden.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (65, 'bregomil.php', 'Man kann sich für 1-5 Edelsteine Übungsgeräte anfertigen lassen, die zufällig genau wie ein Spieler nach Wahl aussehen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (66, 'uhr.php', 'Spieler findet eine Uhr und kann an den Zeigern drehen. Er wird dadurch jünger oder älter, kann dabei auch umkommen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (67, 'earthshrine.php', 'Nettes, kleines Behemoth-Monster, bei Sieg Chance, das Mal der Erde zu erlangen.', 1, 3, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (68, 'kubus.php', 'Ein Fremder, der einen verführen will.. kann sich auch als \\"netter\\" Incubus/Succubus herausstellen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (69, 'may.php', 'Aus Trophäen lässt sich etwas fertigen\r\nund im Haus verwenden...', 2, 5, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (70, 'wolves.php', 'Ein Knappe kann sich als nützlich erweisen und eine Stufe aufsteigen... oder sterben.', 3, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (71, 'goblin.php', 'Triff einen Goblin im Wald, der Dir etwas zu trinken anbietet.', 0, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (72, 'ogre.php', 'Verwertung von Trophäen mit doch recht netten Belohnungen.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (73, 'frogger.php', 'Man soll einen Frosch/eine Kröte von ihrem Fluch befreien. Es kann eine Belohnung geben, oder aber der Fluch geht auf einen selbst über.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (74, 'gruft.php', 'Kleiner Dungeon mit der Möglichkeit, auf Gegner zu treffen, Insigniensplitter und andere Schätze abzuräumen.', 3, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (75, 'magicdoor.php', 'Eine Tür im Wald. Kann WK geben, LP, Gold, Gems, Gefallen.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (77, 'trunktrap.php', 'Ein Baumstamm, der auf den Spieler zurast. Da kann man nur noch hoffen, die richtige Entscheidung getroffen zu haben.', 3, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (78, 'findregalia.php', 'Teil des Gildensystems, Finden von Insigniensplittern.', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (79, 'waldmaer.php', 'Ein kleines Mädchen, das sich verlaufen hat - oder doch ein Monster, das so nur ahnungslose Wanderer täuschen will?', 1, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (80, 'calevents.php', 'Datumsabhängige Ereignisse.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (81, 'charlie.php', 'Wer den Server ärgert, muss sich ausloggen \r\n; )', 3, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (82, 'randomyom.php', 'Eine blinde Brieftaube liefert eine Nachricht aus... an irgendwen... vielleicht...', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (83, 'cairn.php', 'Eines von 5 einzigartigen Idolen, die besondere Kräfte verleihen.', 2, 0, 0, 1);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (84, 'deadmask.php', 'Diese Maske lässt dich den Tag erneut erleben. Aber vorsicht vor dem Dahakra', 1, 0, 0, 0);
REPLACE `waldspecial` (`row_id`, `filename`, `descr`, `prio`, `dk`, `anzahl`, `released`) VALUES (85, 'kudzu.php', 'Chance auf Macadamia-Nüsse und eine kleine Spielerfalle.', 1, 0, 0, 1);

